import { useState } from 'react';
import Checkbox from '@/components/ui/Checkbox';
import Card from '@/components/ui/Card';
import toolsData from '@/data/tools.json';

interface TechStackSelectorProps {
  selectedTools: string[];
  onToolsChange: (tools: string[]) => void;
}

export default function TechStackSelector({ selectedTools, onToolsChange }: TechStackSelectorProps) {
  const toggleTool = (toolId: string) => {
    if (selectedTools.includes(toolId)) {
      onToolsChange(selectedTools.filter(id => id !== toolId));
    } else {
      onToolsChange([...selectedTools, toolId]);
    }
  };

  const groupedTools = {
    'Identity & Access Management': ['okta', 'microsoft-entra', 'jumpcloud', 'google-workspace'],
    'ITSM Platforms': ['servicenow', 'jira', 'freshservice', 'ivanti'],
    'Communication': ['slack', 'microsoft-teams', 'zoom'],
    'Productivity & Collaboration': ['google-workspace', 'microsoft-365', 'notion', 'confluence', 'monday'],
    'Business Applications': ['salesforce', 'hubspot', 'sap-successfactors', 'hibob', 'docusign', 'linear'],
    'Development Tools': ['github', 'jira'],
  };

  return (
    <div className="max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold text-text-primary mb-4">
        What's Your Current IT Stack?
      </h2>
      <p className="text-text-secondary mb-8">
        Select all the tools your team uses. We'll show you exactly which pre-built AI Workers are ready for your environment.
      </p>

      <div className="space-y-6">
        {Object.entries(groupedTools).map(([category, toolIds]) => (
          <Card key={category} className="!bg-bg-card !border !border-bg-card-alt/20">
            <h3 className="text-lg font-semibold text-text-primary mb-4">{category}</h3>
            <div className="grid md:grid-cols-2 gap-4">
              {toolIds.map(toolId => {
                const tool = toolsData.tools.find(t => t.id === toolId);
                if (!tool) return null;

                return (
                  <div key={toolId} className="flex items-center gap-3">
                    <img 
                      src={tool.logo} 
                      alt={tool.name}
                      className="w-6 h-6 rounded"
                      onError={(e) => {
                        (e.target as HTMLImageElement).style.display = 'none';
                      }}
                    />
                    <Checkbox
                      label={tool.name}
                      checked={selectedTools.includes(toolId)}
                      onChange={() => toggleTool(toolId)}
                    />
                  </div>
                );
              })}
            </div>
          </Card>
        ))}
      </div>

      <div className="mt-6 text-center text-text-tertiary">
        Selected: {selectedTools.length} tools
      </div>
    </div>
  );
}

src/components/assessment/WorkflowRanker.content:
import { useState } from 'react';
import Card from '@/components/ui/Card';
import toolsData from '@/data/tools.json';

interface WorkflowRankerProps {
  selectedWorkflows: string[];
  onWorkflowsChange: (workflows: string[]) => void;
}

export default function WorkflowRanker({ selectedWorkflows, onWorkflowsChange }: WorkflowRankerProps) {
  const workflows = toolsData.workflows;

  const toggleWorkflow = (workflowId: string) => {
    if (selectedWorkflows.includes(workflowId)) {
      onWorkflowsChange(selectedWorkflows.filter(id => id !== workflowId));
    } else if (selectedWorkflows.length < 5) {
      onWorkflowsChange([...selectedWorkflows, workflowId]);
    }
  };

  return (
    <div className="max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold text-text-primary mb-4">
        What Eats Up Most of Your Team's Time?
      </h2>
      <p className="text-text-secondary mb-8">
        Select your top 5 most time-consuming activities
      </p>

      <Card className="!bg-bg-card !border !border-bg-card-alt/20">
        <div className="space-y-3">
          {workflows.map((workflow, index) => {
            const isSelected = selectedWorkflows.includes(workflow.id);
            const rank = selectedWorkflows.indexOf(workflow.id) + 1;

            return (
              <button
                key={workflow.id}
                onClick={() => toggleWorkflow(workflow.id)}
                disabled={!isSelected && selectedWorkflows.length >= 5}
                className={`
                  w-full text-left p-4 rounded-lg transition-all duration-200
                  ${isSelected 
                    ? 'bg-bg-card-alt border border-highlight/40 shadow-glow-highlight' 
                    : 'bg-transparent border border-bg-card-alt/40 hover:border-highlight/30 hover:bg-bg-card-alt/30'
                  }
                  ${!isSelected && selectedWorkflows.length >= 5 ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer'}
                `}
              >
                <div className="flex items-center gap-3">
                  {isSelected && (
                    <span className="flex-shrink-0 w-8 h-8 bg-highlight rounded-full flex items-center justify-center text-bg-primary font-bold">
                      {rank}
                    </span>
                  )}
                  <span className={isSelected ? 'text-text-primary font-medium' : 'text-text-primary'}>
                    {workflow.name}
                  </span>
                </div>
              </button>
            );
          })}
        </div>
      </Card>

      <div className="mt-6 text-center">
        <span className="text-text-tertiary">
          Selected: {selectedWorkflows.length} / 5
        </span>
      </div>
    </div>
  );
}

