'use client';

import { useState, useEffect } from 'react';
import Card from '@/components/ui/Card';

interface VolumeServiceProfileProps {
  monthlyTickets: number;
  ticketDistribution: {
    applications: number;
    hardware: number;
    onboarding: number;
    distributionLists: number;
    network: number;
    security: number;
  };
  onChange: (field: string, value: any) => void;
}

export default function VolumeServiceProfile({
  monthlyTickets,
  ticketDistribution,
  onChange,
}: VolumeServiceProfileProps) {
  const [localDistribution, setLocalDistribution] = useState(ticketDistribution);

  useEffect(() => {
    setLocalDistribution(ticketDistribution);
  }, [ticketDistribution]);

  const handleSliderChange = (category: keyof typeof ticketDistribution, newValue: number) => {
    const updated = { ...localDistribution, [category]: newValue };

    setLocalDistribution(updated);
    onChange('ticketDistribution', updated);
  };

  const total = Object.values(localDistribution).reduce((sum, val) => sum + val, 0);

  const categories = [
    { key: 'applications', label: 'Applications' },
    { key: 'hardware', label: 'Hardware' },
    { key: 'onboarding', label: 'Onboarding/Offboarding' },
    { key: 'distributionLists', label: 'Distribution Lists' },
    { key: 'network', label: 'Network' },
    { key: 'security', label: 'Security' },
  ] as const;

  return (
    <div className="max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold text-text-primary mb-4">
        Volume & Service Profile
      </h2>
      <p className="text-text-tertiary mb-8">
        Help us understand your typical ticket volume and distribution
      </p>

      <Card className="!bg-bg-card !border !border-bg-card-alt/20">
        {/* Monthly Tickets Input */}
        <div className="mb-8">
          <label className="block text-text-primary font-bold mb-2">
            Average Monthly Tickets
          </label>
          <input
            type="number"
            value={monthlyTickets || ''}
            onChange={(e) => onChange('monthlyTickets', parseInt(e.target.value) || 0)}
            className="w-full bg-black border border-bg-card-alt/40 rounded-lg px-4 py-3 text-text-primary focus:outline-none focus:border-highlight/40 transition-colors"
            placeholder="1000"
            min="0"
          />
          <p className="text-sm text-text-tertiary mt-2">
            Approximate number of IT tickets your team handles per month
          </p>
        </div>

        {/* Ticket Distribution Sliders */}
        <div>
          <label className="block text-text-primary font-bold mb-4">
            Ticket Distribution by Category (%)
          </label>

          <div className="space-y-6">
            {categories.map(({ key, label }) => (
              <div key={key}>
                <div className="flex justify-between items-center mb-2">
                  <span className="text-text-primary">{label}</span>
                  <span className="text-text-primary font-bold">{localDistribution[key]}%</span>
                </div>
                <input
                  type="range"
                  min="0"
                  max="100"
                  value={localDistribution[key]}
                  onChange={(e) => handleSliderChange(key, parseInt(e.target.value))}
                  className="w-full h-2 bg-bg-card-alt rounded-lg appearance-none cursor-pointer slider"
                  style={{
                    background: `linear-gradient(to right, #82D895 0%, #82D895 ${localDistribution[key]}%, #41403E ${localDistribution[key]}%, #41403E 100%)`,
                  }}
                />
              </div>
            ))}
          </div>

          <div className="mt-6 pt-4 border-t border-bg-card-alt/20">
            <div className="flex justify-between items-center">
              <span className="text-text-tertiary">Total:</span>
              <span className={`font-bold text-lg ${total === 100 ? 'text-highlight' : 'text-red-400'}`}>
                {total}%
              </span>
            </div>
            {total > 100 && (
              <p className="text-red-400 text-sm mt-2">
                Total percentage cannot exceed 100%. Please adjust your distribution.
              </p>
            )}
            {total < 100 && total > 0 && (
              <p className="text-yellow-500 text-sm mt-2">
                Total should equal 100% to proceed.
              </p>
            )}
          </div>
        </div>
      </Card>

      <style jsx>{`
        .slider::-webkit-slider-thumb {
          appearance: none;
          width: 20px;
          height: 20px;
          border-radius: 50%;
          background: white;
          cursor: pointer;
          border: 2px solid #82D895;
        }

        .slider::-moz-range-thumb {
          width: 20px;
          height: 20px;
          border-radius: 50%;
          background: white;
          cursor: pointer;
          border: 2px solid #82D895;
        }
      `}</style>
    </div>
  );
}

src/types/types-v3.ts.content:
// V3 Architecture Type Definitions

// ============================================================================
// Assessment Input Types
// ============================================================================

export interface AssessmentInput {
  techStack: string[]; // Tool IDs selected by user
  monthlyTickets: number; // Total monthly ticket volume
  ticketDistribution: {
    // Percentage distribution (must sum to 100)
    [category: string]: number; // e.g., "applications": 24
  };
  additionalContext?: string; // Free-form context from Step 3
}

// ============================================================================
// Engine Output Types
// ============================================================================

// FeasibilityEngine Output
export interface FeasibilityResult {
  tool: string; // Display name (e.g., "Okta")
  available_apis: string[]; // API display names
  available_capabilities: string[]; // All capabilities from APIs
  enabled_use_cases: string[]; // Use case IDs (not names)
  missing_apis: string[]; // API keys blocking use cases
  license_gaps: string[]; // Upgrade messages
  prerequisites: string[]; // Setup requirements
  confidence: number; // 0.90 if use cases enabled, 0.50 otherwise
}

// UseCaseMatcher Output
export interface MatchedUseCase {
  use_case_id: string;
  name: string;
  category: string;
  description: string;
  value_proposition: string;
  fit_score: number; // 0-100
  estimated_monthly_deflection: number; // Tickets automated
  estimated_hours_saved: number; // Hours per month
  confidence: number; // From use case definition
  implementation_effort: "low" | "medium" | "high";
  time_to_value_days: number;
  prerequisites: string[];
  workflow_steps: string[];
  priority: "immediate" | "quick_win" | "future";
  required_tools: string[];
  automation_type?: "full_automation" | "assisted";
}

// ROICalculator Output
export interface ROIResult {
  total_monthly_tickets: number;
  automatable_tickets: number;
  automatable_pct: number; // Rounded to 1 decimal
  total_hours_saved: number; // Raw operational hours (pre-capture)
  expected_hours_saved: number; // Confidence-weighted expected value
  p70_hours_saved: number; // Conservative band (70th percentile)
  p90_hours_saved: number; // Very conservative band (90th percentile)
  capacity_fte: number; // Theoretical capacity freed (hours/2000)
  budget_fte: number; // Realistic budget impact (captured hours / effective hours per FTE)
  fte_equivalent: number; // Backward-compat alias of capacity_fte
  annual_value_usd: number;
  confidence: number; // Percentage (0-100)

  // NEW: Separate full automation vs assisted
  full_automation_tickets: number;
  full_automation_hours: number;
  assisted_tickets: number;
  assisted_hours: number;

  breakdown_by_category: CategoryBreakdown[];
}

export interface CategoryBreakdown {
  category: string;
  tickets: number;
  hours_saved: number; // Rounded to 1 decimal
  confidence: number; // Average of use case confidences
}

// ============================================================================
// Report Data Structure
// ============================================================================

export interface ReportData {
  assessmentInput: AssessmentInput;
  feasibilityResults: FeasibilityResult[];
  matchedUseCases: MatchedUseCase[];
  roiResult: ROIResult;
  generatedAt: string; // ISO timestamp
}

// ============================================================================
// Data Source Types (from JSON files)
// ============================================================================

// tool-apis.json structure
export interface ToolAPIsData {
  tools: {
    [toolKey: string]: {
      name: string;
      category: "identity" | "itsm" | "communication" | "productivity";
      apis: {
        [apiKey: string]: {
          name: string;
          base_url: string;
          endpoints: APIEndpoint[];
          capabilities: string[];
          scopes_required: string[];
          documentation: string;
        };
      };
      license_tiers: {
        [tierName: string]: string[]; // Array of API keys
      };
      prerequisites: string;
    };
  };
}

export interface APIEndpoint {
  method: string;
  path: string;
  description: string;
}

// use-case-mappings.json structure
export interface UseCaseMappingsData {
  use_cases: UseCaseDefinition[];
}

export interface UseCaseDefinition {
  id: string;
  name: string;
  category: string;
  description: string;
  value_proposition: string;
  required_tools: string[];
  required_apis: {
    [toolKey: string]: string[]; // Tool key ‚Üí array of API keys
  };
  ticket_categories: string[]; // Categories this use case handles
  automation_type?: "full_automation" | "assisted";
  automation_rate: number; // 0.0 to 1.0
  implementation_effort: "low" | "medium" | "high";
  time_to_value_days: number;
