src/components/assessment/report/v5/Oppo.content:
'use client';

import { useState } from 'react';
import ConnectedAppLogos from '@/components/ui/ConnectedAppLogos';
import { MatchedUseCase } from '@/types/types-v3';
import { FeasibilityResult } from '@/lib/engines/feasibility-engine';
import useCaseMappings from '@/data/use-case-mappings.json';

interface OpportunityAnalysisProps {
  matchedUseCases: MatchedUseCase[];
  feasibilityResults: FeasibilityResult[];
  topN?: number;
}

export default function OpportunityAnalysis({ 
  matchedUseCases,
  feasibilityResults,
  topN = 10
}: OpportunityAnalysisProps) {
  const [expandedWorkflows, setExpandedWorkflows] = useState<Record<string, boolean>>({});

  // Convert tool names from underscore format to proper names
  const convertToolName = (tool: string): string => {
    const toolMap: Record<string, string> = {
      'okta': 'Okta',
      'servicenow': 'ServiceNow',
      'slack': 'Slack',
      'jira': 'Jira',
      'jira_service_management': 'Jira Service Management',
      'zendesk': 'Zendesk',
      'microsoft_365': 'Microsoft 365',
      'microsoft_entra': 'Microsoft Entra',
      'microsoft_teams': 'Microsoft Teams',
      'google_workspace': 'Google Workspace',
      'google_drive': 'Google Drive',
      'google_docs': 'Google Docs',
      'google_sheets': 'Google Sheets',
      'google_calendar': 'Google Calendar',
      'gmail': 'Gmail',
      'azure_ad': 'Azure AD',
      'salesforce': 'Salesforce',
      'github': 'GitHub',
      'gitlab': 'GitLab',
      'confluence': 'Confluence',
      'asana': 'Asana',
      'monday_com': 'Monday.com',
      'zoom': 'Zoom',
      'teams': 'Teams',
      'workday': 'Workday',
      'bamboohr': 'BambooHR',
      'duo_security': 'Duo Security',
      'cyberark': 'CyberArk',
      'jumpcloud': 'JumpCloud',
      'freshservice': 'Freshservice',
      'ivanti': 'Ivanti',
      'sap_successfactors': 'SAP SuccessFactors',
      'hibob': 'HiBob',
      'docusign': 'DocuSign',
      'linear': 'Linear',
      'sharepoint': 'SharePoint'
    };

    return toolMap[tool.toLowerCase()] || tool.split('_').map(word => 
      word.charAt(0).toUpperCase() + word.slice(1)
    ).join(' ');
  };

  // Get category color based on category name
  const getCategoryColor = (category: string): string => {
    const categoryColors: Record<string, string> = {
      'Identity Access & Compliance': 'bg-accent-green/20 text-accent-green',
      'CRM Integration': 'bg-accent-blue/20 text-accent-blue',
      'Knowledge Management': 'bg-purple-500/20 text-purple-400',
      'IT Service Management': 'bg-accent-orange/20 text-accent-orange',
      'HR & Onboarding': 'bg-pink-500/20 text-pink-400',
      'Procurement': 'bg-yellow-500/20 text-yellow-400',
      'Project Management': 'bg-indigo-500/20 text-indigo-400',
      'Communication & Collaboration': 'bg-teal-500/20 text-teal-400'
    };

    return categoryColors[category] || 'bg-accent-green/20 text-accent-green';
  };

  // Get all enabled use case IDs
  const enabledUseCaseIds = new Set(
    (feasibilityResults || []).flatMap(result => result.enabled_use_cases)
  );

  // Build comprehensive list: matched use cases PLUS enabled use cases without volume matches
  const allOpportunities: MatchedUseCase[] = [];

  // First, add all matched use cases (these have impact data)
  allOpportunities.push(...matchedUseCases);

  // Then, add enabled use cases that weren't matched (no volume data, but still possible)
  const matchedIds = new Set(matchedUseCases.map(uc => uc.use_case_id));

  (useCaseMappings as any).use_cases.forEach((uc: any) => {
    if (enabledUseCaseIds.has(uc.id) && !matchedIds.has(uc.id)) {
      // Create a pseudo-matched use case with estimated impact based on typical values
      const estimatedVolume = Math.round((uc.typical_volume_pct || 0.05) * 1000); // Assume 1000 tickets baseline
      const estimatedHours = estimatedVolume * (uc.typical_ttr_hours || 1) * (uc.automation_rate || 0.8);

      allOpportunities.push({
        use_case_id: uc.id,
        name: uc.name,
        category: uc.category,
        description: uc.description,
        value_proposition: uc.value_proposition,
        fit_score: 45, // Base score for enabled but unmatched
        estimated_monthly_deflection: estimatedVolume,
        estimated_hours_saved: estimatedHours,
        confidence: uc.confidence * 0.7, // Lower confidence since no volume match
        implementation_effort: uc.implementation_effort,
        time_to_value_days: uc.time_to_value_days,
        prerequisites: uc.prerequisites,
        workflow_steps: uc.workflow_steps,
        priority: uc.time_to_value_days <= 7 ? 'immediate' : 
                  uc.time_to_value_days <= 21 ? 'quick_win' : 'future',
        required_tools: uc.required_tools,
        automation_type: uc.automation_type
      });
    }
  });

  // Sort by fit score and limit to topN
  const topUseCases = allOpportunities
    .sort((a, b) => b.fit_score - a.fit_score)
    .slice(0, topN);

  if (topUseCases.length === 0) {
    return null;
  }

  const getFitScoreColor = (score: number) => {
    if (score >= 80) return 'text-accent-green';
    if (score >= 60) return 'text-accent-blue';
    if (score >= 40) return 'text-accent-orange';
    return 'text-text-tertiary';
  };

  const getFitScoreLabel = (score: number) => {
    if (score >= 80) return 'Excellent Fit';
    if (score >= 60) return 'Good Fit';
    if (score >= 40) return 'Moderate Fit';
    return 'Basic Fit';
  };

  const getPriorityStatus = (priority: string): 'success' | 'active' | 'warning' => {
    if (priority === 'immediate') return 'success';
    if (priority === 'quick_win') return 'active';
    return 'warning';
  };

  const getPriorityLabel = (priority: string) => {
    if (priority === 'immediate') return 'Deploy Week 1';
    if (priority === 'quick_win') return 'Deploy Month 1-2';
    return 'Deploy Month 3+';
  };

  // Separate full automation and assisted use cases
  const fullAutomationCases = topUseCases.filter(uc => uc.automation_type === 'full_automation');
  const assistedCases = topUseCases.filter(uc => uc.automation_type === 'assisted');

  const renderUseCaseCard = (useCase: any, index: number) => (
    <div 
      key={useCase.use_case_id}
      className="relative bg-bg-card rounded-lg p-6 border border-brand-secondary/10 hover:border-highlight/50 transition-all duration-200 overflow-hidden"
    >
      {/* Top Section: Rank, Title, and Tool Logos */}
      <div className="flex items-start justify-between mb-2">
        <div className="flex-1">
          {/* Rank Number */}
          <div className="text-sm font-medium mb-2" style={{ color: '#8a8784' }}>
            {String(index + 1).padStart(2, '0')}
          </div>

          {/* Title */}
          <h3 className="text-2xl font-bold text-text-primary mb-2">
            {useCase.name}
          </h3>

          {/* Category Badge */}
          <div className={`inline-flex px-3 py-1 rounded-full text-xs font-medium ${getCategoryColor(useCase.category)}`}>
            {useCase.category}
          </div>
        </div>

        {/* Tool Logos - Prominent Top Right */}
        {useCase.required_tools && useCase.required_tools.length > 0 && (
          <div className="ml-6 flex-shrink-0">
            <ConnectedAppLogos 
              apps={useCase.required_tools.map(convertToolName)}
              maxVisible={3}
              size={48}
              prominent={true}
            />
          </div>
        )}
      </div>

      {/* Description */}
      <p className="text-lg text-text-secondary mb-6 leading-relaxed">
        {useCase.value_proposition}
      </p>

      {/* Impact Metrics - Enhanced */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-6 p-5 bg-bg-primary/50 rounded-lg border border-border/50 mb-6">
        <div>
          <div className="text-3xl font-bold text-accent-green mb-1">
            {useCase.estimated_monthly_deflection.toLocaleString()}
          </div>
          <div className="text-xs text-text-tertiary uppercase tracking-wide">Tickets/Month</div>
        </div>
        <div>
          <div className="text-3xl font-bold text-accent-blue mb-1">
            {Math.round(useCase.estimated_hours_saved).toLocaleString()} <span className="text-xl">hrs</span>
          </div>
          <div className="text-xs text-text-tertiary uppercase tracking-wide">Time Saved/Month</div>
        </div>
        <div>
          <div className="text-3xl font-bold text-text-primary mb-1">
            {useCase.time_to_value_days} <span className="text-xl">days</span>
          </div>
          <div className="text-xs text-text-tertiary uppercase tracking-wide">Time to Value</div>
        </div>
        <div>
          <div className="text-3xl font-bold text-accent-orange mb-1">
            {Math.round(useCase.confidence * 100)}%
          </div>
          <div className="text-xs text-text-tertiary uppercase tracking-wide">Confidence</div>
        </div>
      </div>

      {/* How It Works - Collapsible */}
      <div>
        <button
          onClick={() => setExpandedWorkflows(prev => ({
            ...prev,
            [useCase.use_case_id]: !prev[useCase.use_case_id]
          }))}
          className="flex items-center gap-2 text-sm font-semibold text-text-primary hover:text-highlight transition-colors duration-200 w-full text-left"
        >
          <span>How it works</span>
          <svg
            className={`w-4 h-4 transition-transform duration-200 ${expandedWorkflows[useCase.use_case_id] ? 'rotate-180' : ''}`}
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
          </svg>
        </button>

        {expandedWorkflows[useCase.use_case_id] && (
          <ul className="space-y-2 mt-4 pl-1">
            {useCase.workflow_steps.map((step: string, i: number) => (
              <li key={i} className="text-sm text-text-secondary flex items-start gap-3">
                <span className="text-highlight mt-0.5 flex-shrink-0 font-bold">‚Üí</span>
                <span>{step}</span>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );

  return (
    <div className="max-w-5xl mx-auto mb-16">
      {fullAutomationCases.length > 0 && (
        <div className="mb-12">
          <div className="mb-8">
            <h2 className="text-3xl font-bold text-text-primary mb-3">
              Full Automation Opportunities
            </h2>
            <p className="text-lg text-text-secondary">
              These use cases can be completely automated end-to-end, requiring no human intervention. Deploy these first for maximum impact.
            </p>
          </div>
          <div className="grid grid-cols-1 gap-6">
            {fullAutomationCases.map((useCase, index) => renderUseCaseCard(useCase, index))}
          </div>
        </div>
      )}

      {assistedCases.length > 0 && (
        <div>
          <div className="mb-8">
            <h2 className="text-3xl font-bold text-text-primary mb-3">
              AI-Assisted Opportunities
            </h2>
            <p className="text-lg text-text-secondary">
              These use cases benefit from AI assistance to augment human work, providing recommendations and automating parts of the workflow.
            </p>
          </div>
          <div className="grid grid-cols-1 gap-6">
            {assistedCases.map((useCase, index) => renderUseCaseCard(useCase, index))}
          </div>
        </div>
      )}
    </div>
  );
}

src/app/report/v5/[id]/page.tsx.content:
"use client";

import { useEffect, useState } from "react";
import { useRouter, useParams } from "next/navigation";
import Link from "next/link";
import Image from "next/image";
import { AssessmentData, ReadinessScore } from "@/types";
import { calculateReadinessScore } from "@/lib/scoringAlgorithm";
import {
  FeasibilityEngine,
  FeasibilityResult,
} from "@/lib/engines/feasibility-engine";
import { UseCaseMatcher, MatchedUseCase } from "@/lib/engines/use-case-matcher";
import { ROICalculator, ROIResult } from "@/lib/engines/roi-calculator";
import ExecutiveSummary from "@/components/assessment/report/v5/ExecutiveSummary";
import OpportunityAnalysis from "@/components/assessment/report/v5/OpportunityAnalysis";
import ExpectedOutcomes from "@/components/assessment/report/v5/ExpectedOutcomes";
import CustomReportCTA from "@/components/assessment/report/v5/CustomReportCTA";

export default function ReportV5Page() {
  const router = useRouter();
  const params = useParams();
  const [loading, setLoading] = useState(true);
  const [score, setScore] = useState<ReadinessScore | null>(null);
  const [assessmentData, setAssessmentData] = useState<AssessmentData | null>(
    null,
  );
  const [copied, setCopied] = useState(false);

  const [feasibilityResults, setFeasibilityResults] = useState<
    FeasibilityResult[]
  >([]);
  const [matchedUseCases, setMatchedUseCases] = useState<MatchedUseCase[]>([]);
  const [roiResult, setRoiResult] = useState<ROIResult | null>(null);

  const handleShare = () => {
    navigator.clipboard.writeText(window.location.href);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  useEffect(() => {
    async function loadAssessmentData() {
      const id = params.id as string;

      // If ID is "new", load from sessionStorage
      if (id === "new") {
        const dataStr = sessionStorage.getItem("assessmentData");
        if (!dataStr) {
          router.push("/");
          return;
        }
        const data: AssessmentData = JSON.parse(dataStr);
        setAssessmentData(data);
        processAssessmentData(data);
        return;
      }

      // Otherwise, load from database
      try {
        const response = await fetch(`/api/assessments/${id}`);
        const result = await response.json();

        if (result.success && result.data) {
          const data = result.data.reportData as AssessmentData;
          setAssessmentData(data);
          processAssessmentData(data);
        } else {
          // Fallback to sessionStorage
          const dataStr = sessionStorage.getItem("assessmentData");
          if (dataStr) {
            const data: AssessmentData = JSON.parse(dataStr);
            setAssessmentData(data);
            processAssessmentData(data);
          } else {
            router.push("/");
          }
        }
      } catch (error) {
        console.error("Error loading assessment:", error);
        // Fallback to sessionStorage
        const dataStr = sessionStorage.getItem("assessmentData");
        if (dataStr) {
          const data: AssessmentData = JSON.parse(dataStr);
          setAssessmentData(data);
          processAssessmentData(data);
        } else {
          router.push("/");
        }
      }
    }

    loadAssessmentData();
  }, [params.id, router]);

  function processAssessmentData(data: AssessmentData) {
    setTimeout(() => {
      const calcScore = calculateReadinessScore(data);
      setScore(calcScore);

      const feasibilityEngine = new FeasibilityEngine();
      const useCaseMatcher = new UseCaseMatcher();
      const roiCalculator = new ROICalculator();

      const tools =
        data.techStack?.map((toolName) => ({
          name: toolName,
          license_tier: "standard",
        })) || [];

      const activities = [];
      if (data.ticketDistribution && data.monthlyTickets) {
        const categoryMapping: Record<string, { key: string; ttr: number }> = {
          applications: { key: "app_access", ttr: 1.7 },
          hardware: { key: "hardware", ttr: 2.5 },
          onboarding: { key: "onboarding", ttr: 4.0 },
          distributionLists: { key: "distribution_lists", ttr: 0.75 },
          network: { key: "network", ttr: 1.5 },
          security: { key: "security", ttr: 3.0 },
        };

        for (const [key, percentage] of Object.entries(
          data.ticketDistribution,
        )) {
          const mapping = categoryMapping[key];
          if (mapping && percentage > 0) {
            activities.push({
              category: mapping.key,
              monthly_volume: Math.round(
                (data.monthlyTickets * percentage) / 100,
              ),
              avg_ttr_hours: mapping.ttr,
            });
          }
        }
      }

      const feasibility = feasibilityEngine.analyzeStack(tools);
      const matches = useCaseMatcher.matchUseCases(activities, feasibility);
      const roi = roiCalculator.calculateROI(
        data.monthlyTickets || 1000,
        matches,
      );

      setFeasibilityResults(feasibility);
      setMatchedUseCases(matches);
      setRoiResult(roi);
      setLoading(false);
    }, 3000);
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-bg-primary flex items-center justify-center">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-16 w-16 border-4 border-highlight border-t-transparent mb-8" />
          <h2 className="text-2xl font-bold text-text-primary mb-2">
            Analyzing your environment...
          </h2>
          <p className="text-text-tertiary">
            Running AI Worker readiness analysis
          </p>
        </div>
      </div>
    );
  }

  if (!score || !assessmentData) return null;

  return (
    <div className="min-h-screen bg-bg-primary">
      {/* Header */}
      <header className="border-b border-bg-card-alt/20">
        <div className="max-w-7xl mx-auto px-6 py-4 flex items-center justify-between">
          <Link href="/">
            <Image
              src="/images/aiwork-logo.png"
              alt="ai.work"
              width={120}
              height={30}
              className="h-7 w-auto"
            />
          </Link>
          <button
            onClick={handleShare}
            className="flex items-center gap-2 px-4 py-2 bg-white text-black rounded-lg font-semibold hover:bg-gray-100 transition-colors"
          >
            <span className="w-2 h-2 rounded-full bg-highlight"></span>
            <span className="text-sm">
              {copied ? "Copied!" : "Share this"}
            </span>
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8.684 13.342C8.886 12.938 9 12.482 9 12c0-.482-.114-.938-.316-1.342m0 2.684a3 3 0 110-2.684m0 2.684l6.632 3.316m-6.632-6l6.632-3.316m0 0a3 3 0 105.367-2.684 3 3 0 00-5.367 2.684zm0 9.316a3 3 0 105.368 2.684 3 3 0 00-5.368-2.684z" />
            </svg>
          </button>
        </div>
      </header>

      {/* Main */}
      <main className="py-12">
        <div className="container mx-auto px-4 max-w-7xl">
          {/* Heading */}
          <div className="mb-12">
            <h1 className="text-4xl md:text-5xl font-bold mb-4 text-text-primary">
              AI Worker Readiness Assessment
            </h1>
            <p className="text-xl text-text-secondary">
              Analysis complete for {assessmentData.techStack?.length || 0} connected tools and{' '}
              {(assessmentData.monthlyTickets || 0).toLocaleString()} monthly tickets
            </p>
          </div>

          {/* Hero Statistics */}
          {roiResult && (
            <ExecutiveSummary
              roiResult={roiResult}
              totalMonthlyTickets={assessmentData.monthlyTickets || 1000}
            />
          )}

          {/* Use Case Cards */}
          <OpportunityAnalysis
            matchedUseCases={matchedUseCases}
            feasibilityResults={feasibilityResults}
            topN={10}
          />

          <div className="border-t border-bg-card-alt/20 mb-20"></div>

          {/* What to Expect */}
          {roiResult && (
            <ExpectedOutcomes
              roiResult={roiResult}
              totalMonthlyTickets={assessmentData.monthlyTickets || 1000}
            />
          )}

          <div className="border-t border-bg-card-alt/20 mb-20"></div>

          {/* Call to Action */}
          <CustomReportCTA />
        </div>
      </main>
    </div>
  );
}

src/components/assessment/report/v5/ROIB.content:
'use client';

import { ROIResult } from '@/lib/engines/roi-calculator';
import { FeasibilityResult } from '@/lib/engines/feasibility-engine';
import Card from '@/components/ui/Card';
import useCaseMappings from '@/data/use-case-mappings.json';
import { formatLargeNumber } from '@/lib/utils/formatNumber';

interface ROIBreakdownProps {
  roiResult: ROIResult;
  feasibilityResults: FeasibilityResult[];
}

export default function ROIBreakdown({ roiResult, feasibilityResults }: ROIBreakdownProps) {
  // Get all enabled use case IDs
  const enabledUseCaseIds = new Set(
    (feasibilityResults || []).flatMap(result => result.enabled_use_cases)
  );

  // Get categories from matched use cases (actual impact)
  const matchedCategories = new Set(
    roiResult.breakdown_by_category.map(cat => cat.category)
  );

  // Find additional categories with enabled use cases
  const additionalCategories: Record<string, {
    category: string;
    useCases: any[];
    estimatedTickets: number;
    estimatedHours: number;
  }> = {};

  (useCaseMappings as any).use_cases.forEach((uc: any) => {
    // Filter out Meetings & Collaboration Licenses category
    if (uc.category === 'Meetings & Collaboration Licenses') {
      return;
    }

    if (enabledUseCaseIds.has(uc.id) && !matchedCategories.has(uc.category)) {
      if (!additionalCategories[uc.category]) {
        additionalCategories[uc.category] = {
          category: uc.category,
          useCases: [],
          estimatedTickets: 0,
          estimatedHours: 0
        };
      }
      additionalCategories[uc.category].useCases.push(uc);
      // Estimate based on typical values (assuming 1000 ticket baseline)
      const estTickets = Math.round((uc.typical_volume_pct || 0.05) * 1000 * (uc.automation_rate || 0.8));
      const estHours = estTickets * (uc.typical_ttr_hours || 1.5);
      additionalCategories[uc.category].estimatedTickets += estTickets;
      additionalCategories[uc.category].estimatedHours += estHours;
    }
  });
  return (
    <section className="mb-20">
      <h2 className="text-4xl font-bold text-text-primary mb-4">
        ROI Breakdown
      </h2>
      <p className="text-xl text-text-tertiary mb-12">
        Detailed impact analysis by category
      </p>

      {/* Summary Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-12">
        <Card className="!bg-bg-card !border !border-bg-card-alt/20">
          <div className="text-sm text-text-tertiary mb-2">Automatable Tickets</div>
          <div className="text-3xl font-bold text-highlight">
            {roiResult.automatable_tickets.toLocaleString()}
            <span className="text-lg text-text-tertiary ml-2">
              ({roiResult.automatable_pct.toFixed(1)}%)
            </span>
          </div>
        </Card>

        <Card className="!bg-bg-card !border !border-bg-card-alt/20">
          <div className="text-sm text-text-tertiary mb-2">Hours Saved/Month</div>
          <div className="text-3xl font-bold text-highlight">
            {Math.round(roiResult.total_hours_saved).toLocaleString()}
          </div>
        </Card>

        <Card className="!bg-bg-card !border !border-bg-card-alt/20">
          <div className="text-sm text-text-tertiary mb-2">FTE Equivalent</div>
          <div className="text-3xl font-bold text-highlight">
            {roiResult.fte_equivalent.toFixed(1)}
          </div>
        </Card>

        <Card className="!bg-bg-card !border !border-bg-card-alt/20">
          <div className="text-sm text-text-tertiary mb-2">Annual Value</div>
          <div className="text-3xl font-bold text-highlight">
            {formatLargeNumber(roiResult.annual_value_usd, '$')}
          </div>
        </Card>
      </div>

      {/* Category Breakdown */}
      <Card className="!bg-bg-card !border !border-bg-card-alt/20">
        <h3 className="text-2xl font-bold text-text-primary mb-6">
          Impact by Category
        </h3>

        {/* Matched Categories with Actual Impact */}
        <div className="space-y-6 mb-8">
          {roiResult.breakdown_by_category
            .filter(cat => cat.category !== 'Meetings & Collaboration Licenses')
            .map((category, idx) => (
            <div key={idx} className="border-b border-bg-card-alt/20 pb-6">
              <div className="flex items-start justify-between mb-3">
                <div>
                  <h4 className="text-lg font-semibold text-text-primary">
                    {category.category}
                  </h4>
                  <p className="text-sm text-text-tertiary">
                    {category.tickets.toLocaleString()} tickets/month automated
                  </p>
                </div>
                <span className="px-3 py-1 rounded-full bg-highlight/20 text-highlight text-sm font-medium">
                  {Math.round(category.confidence * 100)}% confidence
                </span>
              </div>

              <div className="flex items-center gap-4">
                <div className="flex-1">
                  <div className="text-2xl font-bold text-highlight">
                    {Math.round(category.hours_saved).toLocaleString()} hours/month
                  </div>
                  <div className="text-sm text-text-tertiary">
                    ~{((category.hours_saved * 12) / 2000).toFixed(1)} FTE saved annually
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Additional Categories with Estimated Potential */}
        {Object.keys(additionalCategories).length > 0 && (
          <>
            <div className="border-t border-bg-card-alt/20 pt-8 mb-6">
              <h4 className="text-lg font-semibold text-text-primary mb-2">
                Additional Automation Potential
              </h4>
              <p className="text-sm text-text-tertiary">
                More categories you can automate with your current capabilities
              </p>
            </div>

            <div className="space-y-6">
              {Object.values(additionalCategories).map((cat, idx) => (
                <div key={idx} className="border-b border-bg-card-alt/20 last:border-0 pb-6 last:pb-0">
                  <div className="flex items-start justify-between mb-3">
                    <div>
                      <h5 className="text-lg font-semibold text-text-primary">
                        {cat.category}
                      </h5>
                      <p className="text-sm text-text-tertiary">
                        {cat.useCases.length} AI Worker{cat.useCases.length !== 1 ? 's' : ''} available
                      </p>
                    </div>
                    <span className="px-3 py-1 rounded-full bg-accent-blue/20 text-accent-blue text-sm font-medium">
                      Estimated
                    </span>
                  </div>

                  <div className="flex items-center gap-4">
                    <div className="flex-1">
                      <div className="text-2xl font-bold text-accent-blue">
                        ~{Math.round(cat.estimatedHours)} hours/month
                      </div>
                      <div className="text-sm text-text-tertiary">
                        Potential savings based on typical use patterns
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </>
        )}

