'use client';

import { useState, useEffect, useRef } from 'react';
import { X, Info } from 'lucide-react';

interface ROICalculationModalProps {
  trigger?: React.ReactNode;
  className?: string;
}

export default function ROICalculationModal({ trigger, className = '' }: ROICalculationModalProps) {
  const [isOpen, setIsOpen] = useState(false);
  const modalRef = useRef<HTMLDivElement>(null);
  const triggerRef = useRef<HTMLElement | null>(null);
  const closeButtonRef = useRef<HTMLButtonElement>(null);

  const handleOpen = (e: React.MouseEvent | React.KeyboardEvent) => {
    triggerRef.current = e.currentTarget as HTMLElement;
    setIsOpen(true);
  };

  const handleClose = () => {
    setIsOpen(false);
    // Restore focus to trigger element
    setTimeout(() => {
      triggerRef.current?.focus();
    }, 0);
  };

  // Handle Escape key
  useEffect(() => {
    if (!isOpen) return;

    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape') {
        handleClose();
      }
    };

    document.addEventListener('keydown', handleEscape);
    return () => document.removeEventListener('keydown', handleEscape);
  }, [isOpen]);

  // Focus management and trap
  useEffect(() => {
    if (!isOpen || !modalRef.current) return;

    // Move focus to close button or modal container
    const initialFocus = closeButtonRef.current || modalRef.current;
    initialFocus?.focus();

    // Focus trap
    const handleTab = (e: KeyboardEvent) => {
      if (e.key !== 'Tab' || !modalRef.current) return;

      const focusableElements = modalRef.current.querySelectorAll(
        'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])'
      );

      // Guard against empty or single-element lists
      if (focusableElements.length === 0) {
        e.preventDefault();
        return;
      }

      const firstFocusable = focusableElements[0] as HTMLElement;
      const lastFocusable = focusableElements[focusableElements.length - 1] as HTMLElement;

      // Single focusable element - prevent tabbing away
      if (focusableElements.length === 1) {
        e.preventDefault();
        firstFocusable?.focus();
        return;
      }

      // Handle backward tab
      if (e.shiftKey) {
        if (document.activeElement === firstFocusable) {
          e.preventDefault();
          lastFocusable?.focus();
        }
      } else {
        // Handle forward tab
        if (document.activeElement === lastFocusable) {
          e.preventDefault();
          firstFocusable?.focus();
        }
      }
    };

    // Use document listener to catch all tabs
    document.addEventListener('keydown', handleTab);
    return () => document.removeEventListener('keydown', handleTab);
  }, [isOpen]);

  const defaultTrigger = (
    <button
      onClick={handleOpen}
      className="inline-flex items-center gap-1 text-accent-green hover:text-accent-green/80 transition-colors"
      aria-label="How is this calculated?"
    >
      <Info className="w-4 h-4" />
    </button>
  );

  if (!isOpen) {
    return (
      <span className={className}>
        {trigger ? (
          <span onClick={handleOpen} role="button" tabIndex={0} onKeyDown={(e) => e.key === 'Enter' && handleOpen(e)}>
            {trigger}
          </span>
        ) : (
          defaultTrigger
        )}
      </span>
    );
  }

  return (
    <>
      {/* Backdrop */}
      <div
        className="fixed inset-0 bg-black/60 z-50 backdrop-blur-sm"
        onClick={handleClose}
        aria-hidden="true"
        role="presentation"
      />

      {/* Modal */}
      <div className="fixed inset-0 z-50 overflow-y-auto" role="dialog" aria-modal="true" aria-labelledby="modal-title">
        <div className="flex min-h-full items-center justify-center p-4">
          <div
            ref={modalRef}
            tabIndex={-1}
            className="relative bg-[#1a1a1a] border border-brand-secondary/20 rounded-lg shadow-xl max-w-3xl w-full max-h-[85vh] overflow-y-auto focus:outline-none"
            onClick={(e) => e.stopPropagation()}
          >
            {/* Header */}
            <div className="sticky top-0 bg-[#1a1a1a] border-b border-brand-secondary/20 px-6 py-4 flex items-center justify-between">
              <h2 id="modal-title" className="text-2xl font-bold text-text-primary">How We Calculate Your ROI</h2>
              <button
                ref={closeButtonRef}
                onClick={handleClose}
                className="text-text-secondary hover:text-text-primary transition-colors"
                aria-label="Close modal"
              >
                <X className="w-6 h-6" />
              </button>
            </div>

            {/* Content */}
            <div className="px-6 py-6 space-y-8 text-text-secondary">
              {/* The Short Version */}
              <section>
                <h3 className="text-xl font-semibold text-text-primary mb-3">The Short Version</h3>
                <ol className="list-decimal list-inside space-y-2 text-lg">
                  <li>You tell us your <strong className="text-text-primary">ticket volumes</strong> and the <strong className="text-text-primary">average hands-on time</strong> it takes a person to handle them.</li>
                  <li>Based on your <strong className="text-text-primary">tech stack</strong>, we identify <strong className="text-text-primary">AI Workers</strong> (use cases) that can either fully handle or greatly speed up those tickets.</li>
                  <li>We <strong className="text-text-primary">avoid double-counting</strong> by only using the remaining tickets in each category as we add use cases.</li>
                  <li>We translate time saved into two views:
                    <ul className="list-disc list-inside ml-6 mt-2 space-y-1">
                      <li><strong className="text-text-primary">Capacity FTE</strong> (operational headroom)</li>
                      <li><strong className="text-text-primary">Budget FTE</strong> (realistic financial impact)</li>
                    </ul>
                  </li>
                  <li>We apply <strong className="text-text-primary">confidence</strong> and show <strong className="text-text-primary">Expected / P70 / P90</strong> ranges so you can choose how conservative you want to be.</li>
                </ol>
              </section>

              {/* Key Concepts */}
              <section>
                <h3 className="text-xl font-semibold text-text-primary mb-3">Key Concepts</h3>

                <div className="space-y-4">
                  <div className="p-4 bg-accent-green/10 border border-accent-green/30 rounded-lg">
                    <h4 className="font-semibold text-accent-green mb-2">Capacity FTE vs Budget FTE</h4>
                    <p className="text-lg"><strong className="text-text-primary">Capacity FTE</strong> shows operational headroom (hours saved converted to an annualized FTE using 2,000 hours/FTE). It's useful for planning workload and SLAs.</p>
                    <p className="text-lg mt-2"><strong className="text-text-primary">Budget FTE</strong> reflects what typically turns into budget-relevant capacity. We apply a <strong className="text-text-primary">capture rate</strong> (how much saved time becomes truly usable‚Äîdefault 50%) and <strong className="text-text-primary">effective hours per FTE</strong> (realistic annual working hours‚Äîdefault 1,800).</p>
                  </div>

                  <div className="p-4 bg-accent-blue/10 border border-accent-blue/30 rounded-lg">
                    <h4 className="font-semibold text-accent-blue mb-2">Confidence Bands</h4>
                    <p className="text-lg">Not every organization sees the same outcomes. Each worker has a confidence score. We weight savings by confidence and present:</p>
                    <ul className="list-disc list-inside ml-4 mt-2 space-y-1 text-lg">
                      <li><strong className="text-text-primary">Expected</strong> (our best estimate)</li>
                      <li><strong className="text-text-primary">P70</strong> (conservative)</li>
                      <li><strong className="text-text-primary">P90</strong> (more conservative)</li>
                    </ul>
                    <p className="text-lg mt-2">This gives you a range to share with stakeholders depending on your risk tolerance.</p>
                  </div>
                </div>
              </section>

              {/* How Savings Are Calculated */}
              <section>
                <h3 className="text-xl font-semibold text-text-primary mb-3">How Savings Are Calculated</h3>
                <div className="space-y-3 text-lg">
                  <p>For each matching AI Worker:</p>
                  <ul className="list-disc list-inside ml-4 space-y-2">
                    <li>We determine how many tickets it can handle from the <strong className="text-text-primary">remaining pool</strong> in that category.</li>
                    <li>We estimate <strong className="text-text-primary">time saved per ticket</strong>:
                      <ul className="list-disc list-inside ml-6 mt-1 space-y-1">
                        <li>If the task is fully automated: the <strong className="text-text-primary">full hands-on time</strong> is saved.</li>
                        <li>If it still needs a quick review/approval: we save the <strong className="text-text-primary">difference</strong> between the original time and the short micro-touch (typically ~5 minutes).</li>
                      </ul>
                    </li>
                    <li>If a small share of tickets still require a person (approvals/exceptions), we reduce the savings for those.</li>
                  </ul>
                  <p className="mt-3">We add up the hours across all workers to get <strong className="text-text-primary">total hours saved per month</strong>. If totals ever exceed your overall ticket count, we automatically <strong className="text-text-primary">cap and scale</strong> the result so it stays realistic.</p>
                </div>
              </section>

              {/* Guardrails */}
              <section>
                <h3 className="text-xl font-semibold text-text-primary mb-3">Guardrails That Keep Results Honest</h3>
                <div className="grid md:grid-cols-2 gap-3">
                  <div className="p-3 bg-bg-primary/50 rounded border border-brand-secondary/10">
                    <span className="text-accent-green font-medium">‚úì</span> No double-counting within a category
                  </div>
                  <div className="p-3 bg-bg-primary/50 rounded border border-brand-secondary/10">
                    <span className="text-accent-green font-medium">‚úì</span> Caps to prevent saving more tickets than you have
                  </div>
                  <div className="p-3 bg-bg-primary/50 rounded border border-brand-secondary/10">
                    <span className="text-accent-green font-medium">‚úì</span> Confidence-weighted outcomes
                  </div>
                  <div className="p-3 bg-bg-primary/50 rounded border border-brand-secondary/10">
                    <span className="text-accent-green font-medium">‚úì</span> Conservative defaults for micro-touch and capture rate
                  </div>
                </div>
              </section>

              {/* FAQs */}
              <section>
                <h3 className="text-xl font-semibold text-text-primary mb-3">Frequently Asked Questions</h3>
                <div className="space-y-4">
                  <div>
                    <h4 className="font-semibold text-text-primary mb-1">Why two FTE numbers?</h4>
                    <p className="text-lg">Capacity FTE describes operational headroom. Budget FTE is what usually translates into budget or hiring decisions.</p>
                  </div>
                  <div>
                    <h4 className="font-semibold text-text-primary mb-1">Why not just divide by 2,000 and be done?</h4>
                    <p className="text-lg">Because saved minutes rarely convert 1:1 into money. Capture rate and effective hours reflect how teams actually work.</p>
                  </div>
                  <div>
                    <h4 className="font-semibold text-text-primary mb-1">Our tickets still need approvals‚Äîdoes that inflate savings?</h4>
                    <p className="text-lg">No. If a worker still needs a quick human touch, we subtract that micro-touch from the saved time.</p>
                  </div>
                  <div>
                    <h4 className="font-semibold text-text-primary mb-1">Where do the worker confidence scores come from?</h4>
                    <p className="text-lg">From real-world maturity of the automation and how well it fits your stack. We weight savings by those scores.</p>
                  </div>
                </div>
              </section>
            </div>

            {/* Footer */}
            <div className="sticky bottom-0 bg-[#1a1a1a] border-t border-brand-secondary/20 px-6 py-4">
              <button
                onClick={handleClose}
                className="w-full bg-accent-green text-black font-medium py-3 px-6 rounded-lg hover:bg-accent-green/90 transition-colors"
              >
                Got it
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

archive/reports/components/v4/ExpectedOu.content:
import Card from '@/components/ui/Card';
import { ROIResult } from '@/types/types-v3';
import { formatLargeNumber } from '@/lib/utils/formatNumber';

interface ExpectedOutcomesProps {
  roiResult: ROIResult;
  totalMonthlyTickets: number;
}

export default function ExpectedOutcomes({ 
  roiResult, 
  totalMonthlyTickets 
}: ExpectedOutcomesProps) {

  // Calculate additional metrics
  const ticketDeflectionRate = totalMonthlyTickets > 0 
    ? (roiResult.automatable_tickets / totalMonthlyTickets) * 100 
    : 0;
  const avgHoursPerTicket = roiResult.automatable_tickets > 0 
    ? roiResult.total_hours_saved / roiResult.automatable_tickets 
    : 0;
  const monthlyValue = roiResult.annual_value_usd / 12;

  // Learning curve projection (conservative 15% improvement over 6 months)
  const month6Multiplier = 1.15;
  const projectedMonth6Tickets = Math.round(roiResult.automatable_tickets * month6Multiplier);

  // Cap at total monthly tickets (can't automate more than 100%)
  const month6Tickets = Math.min(projectedMonth6Tickets, totalMonthlyTickets);

  // Calculate actual improvement ratio achieved (may be less than 1.15 if capped)
  // Guard against division by zero
  const actualMultiplier = roiResult.automatable_tickets > 0 
    ? month6Tickets / roiResult.automatable_tickets 
    : 0;

  // Apply proportional improvements to hours, FTE, and value
  const month6Hours = roiResult.total_hours_saved * actualMultiplier;
  const month6FTE = roiResult.fte_equivalent * actualMultiplier;
  const month6Value = roiResult.annual_value_usd * actualMultiplier;

  const getIconSvg = (iconType: string) => {
    const icons: Record<string, JSX.Element> = {
      target: (
        <svg className="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
      ),
      clock: (
        <svg className="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
      ),
      currency: (
        <svg className="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
      ),
      lightning: (
        <svg className="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
        </svg>
      ),
      graduation: (
        <svg className="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 14l9-5-9-5-9 5 9 5z" />
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 14l6.16-3.422a12.083 12.083 0 01.665 6.479A11.952 11.952 0 0012 20.055a11.952 11.952 0 00-6.824-2.998 12.078 12.078 0 01.665-6.479L12 14z" />
        </svg>
      ),
      smile: (
        <svg className="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14.828 14.828a4 4 0 01-5.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
      ),
      chart: (
        <svg className="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
        </svg>
      ),
      lock: (
        <svg className="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
        </svg>
      ),
      trending: (
        <svg className="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" />
        </svg>
      ),
      refresh: (
        <svg className="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
        </svg>
      )
    };
    return icons[iconType] || null;
  };

  const outcomes = [
    {
      icon: 'target',
      title: 'Ticket Deflection',
      baseline: {
        label: 'Month 1',
        value: `${roiResult.automatable_tickets.toLocaleString()}`,
        subtitle: `${ticketDeflectionRate.toFixed(1)}% of total volume`
      },
      improved: {
        label: 'Month 6',
        value: `${month6Tickets.toLocaleString()}`,
        subtitle: totalMonthlyTickets > 0 
          ? `${(month6Tickets / totalMonthlyTickets * 100).toFixed(1)}% of total volume`
          : '0.0% of total volume'
      },
      description: 'Tickets handled autonomously without human intervention'
    },
    {
      icon: 'clock',
      title: 'Time Savings',
      baseline: {
        label: 'Month 1',
        value: `${Math.round(roiResult.total_hours_saved).toLocaleString()} hrs`,
        subtitle: `${roiResult.fte_equivalent.toFixed(1)} FTE capacity`
      },
      improved: {
        label: 'Month 6',
        value: `${Math.round(month6Hours).toLocaleString()} hrs`,
        subtitle: `${month6FTE.toFixed(1)} FTE capacity`
      },
      description: 'IT team hours redirected from routine work to strategic initiatives'
    },
    {
      icon: 'currency',
      title: 'Cost Savings',
      baseline: {
        label: 'Monthly',
        value: formatLargeNumber(monthlyValue, '$'),
        subtitle: 'Labor cost savings'
      },
      improved: {
        label: 'Annual',
        value: formatLargeNumber(month6Value, '$'),
        subtitle: 'With learning curve'
      },
      description: 'Fully-loaded labor cost at $100K per FTE'
    },
    {
      icon: 'lightning',
      title: 'Resolution Speed',
      baseline: {
        label: 'Current Avg',
        value: `${Math.round(avgHoursPerTicket * 60).toLocaleString()} min`,
        subtitle: 'Manual processing'
      },
      improved: {
        label: 'With AI Workers',
        value: '< 5 min',
        subtitle: '70-90% faster'
      },
      description: 'Average time from ticket creation to resolution'
    }
  ];

  const qualitativeOutcomes = [
    {
      icon: 'graduation',
      title: 'Continuous Learning',
      description: 'AI Workers improve accuracy over time by learning from your unique policies, edge cases, and ticket patterns'
    },
    {
      icon: 'smile',
      title: 'Employee Satisfaction',
      description: 'End users get faster responses, IT team focuses on meaningful work instead of repetitive tickets'
    },
    {
      icon: 'chart',
      title: 'Operational Insights',
      description: 'Real-time dashboards showing automation rates, ticket patterns, and opportunities for optimization'
    },
    {
      icon: 'lock',
      title: 'Compliance & Audit',
      description: 'Complete audit trails for all automated actions, ensuring compliance and security governance'
    },
    {
      icon: 'trending',
      title: 'Scalable Operations',
      description: 'Handle ticket volume growth without proportional headcount increases'
    },
    {
      icon: 'refresh',
      title: 'Reduced Context Switching',
      description: 'IT team interrupted less frequently, can focus on project work and strategic initiatives'
    }
  ];

  return (
    <div className="max-w-6xl mx-auto mb-16">
      <div className="mb-12">
        <h2 className="text-3xl font-bold text-text-primary mb-4">
          What to Expect
        </h2>
        <p className="text-text-secondary max-w-3xl">
          Projected results from deploying AI Workers with your {totalMonthlyTickets.toLocaleString()} monthly tickets
        </p>
      </div>

      {/* Quantitative Outcomes */}
      <div className="mb-12">
        <h3 className="text-xl font-bold text-text-primary mb-6">
          Measurable Impact
        </h3>
        <div className="grid md:grid-cols-2 gap-6">
          {outcomes.map((outcome, index) => (
            <Card key={index}>
              <div className="flex items-start gap-4 mb-4">
                <div className="flex-shrink-0 w-12 h-12 bg-accent-blue/20 rounded-full flex items-center justify-center text-accent-blue">
                  {getIconSvg(outcome.icon)}
                </div>
                <div className="flex-1">
                  <h4 className="text-lg font-bold text-text-primary mb-1">
                    {outcome.title}
                  </h4>
                  <p className="text-sm text-text-secondary">
                    {outcome.description}
                  </p>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4 p-4 bg-bg-primary rounded-lg">
                <div>
                  <div className="text-xs text-text-tertiary mb-1">
                    {outcome.baseline.label}
                  </div>
                  <div className="text-2xl font-bold text-text-primary mb-1">
                    {outcome.baseline.value}
                  </div>
                  <div className="text-xs text-text-tertiary">
                    {outcome.baseline.subtitle}
                  </div>
                </div>
                <div className="border-l border-brand-secondary/20 pl-4">
                  <div className="text-xs text-accent-green mb-1">
                    {outcome.improved.label}
                  </div>
                  <div className="text-2xl font-bold text-accent-green mb-1">
                    {outcome.improved.value}
                  </div>
                  <div className="text-xs text-text-tertiary">
                    {outcome.improved.subtitle}
                  </div>
                </div>
              </div>
            </Card>
          ))}
        </div>
      </div>

      {/* Qualitative Outcomes */}
      <div className="mb-12">
        <h3 className="text-xl font-bold text-text-primary mb-6">
          Strategic Benefits
        </h3>
        <div className="grid md:grid-cols-3 gap-6">
          {qualitativeOutcomes.map((outcome, index) => (
            <Card key={index} className="text-center">
              <div className="flex justify-center mb-3">
                <div className="w-12 h-12 bg-accent-blue/20 rounded-full flex items-center justify-center text-accent-blue">
                  {getIconSvg(outcome.icon)}
                </div>
              </div>
              <h4 className="text-base font-bold text-text-primary mb-2">
                {outcome.title}
              </h4>
              <p className="text-sm text-text-secondary">
                {outcome.description}
              </p>
            </Card>
          ))}
        </div>
      </div>

      {/* Learning Curve Explanation */}
      <Card className="bg-accent-blue/5 border-accent-blue/20">
        <div className="flex items-start gap-4">
          <div className="flex-shrink-0 w-12 h-12 bg-accent-blue/20 rounded-full flex items-center justify-center">
            <svg className="w-6 h-6 text-accent-blue" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" />
            </svg>
          </div>
          <div className="flex-1">
            <h4 className="text-lg font-bold text-text-primary mb-2">
              Why Performance Improves Over Time
            </h4>
            <p className="text-sm text-text-secondary mb-3">
              Unlike traditional automation with static rules, AI Workers continuously learn from every ticket they process:
            </p>
            <ul className="space-y-2 text-sm text-text-secondary">
              <li className="flex items-start gap-2">
                <span className="text-accent-blue mt-0.5">‚Üí</span>
                <span><strong>Policy learning:</strong> Understand nuances in approval patterns and access policies</span>
              </li>
              <li className="flex items-start gap-2">
                <span className="text-accent-blue mt-0.5">‚Üí</span>
                <span><strong>Edge case handling:</strong> Build knowledge of exceptions and special scenarios</span>
              </li>
              <li className="flex items-start gap-2">
                <span className="text-accent-blue mt-0.5">‚Üí</span>
                <span><strong>Ticket pattern recognition:</strong> Better categorization and routing over time</span>
              </li>
              <li className="flex items-start gap-2">
                <span className="text-accent-blue mt-0.5">‚Üí</span>
                <span><strong>Context awareness:</strong> Learn relationships between users, teams, and resources</span>
              </li>
            </ul>
            <p className="text-xs text-text-tertiary mt-3">
              Conservative estimate: 15% improvement by Month 6. Top performers see 30-40% improvement by Month 12.
            </p>
          </div>
        </div>
      </Card>

      {/* Confidence Disclaimer */}
      <div className="mt-8 text-center">
        <p className="text-sm text-text-tertiary">
          Outcomes based on {roiResult.confidence}% confidence score using your actual tech stack and ticket distribution.
        </p>
      </div>
    </div>
  );
}

archive/reports/components/BestFitUseCas.content:
import Card from '@/components/ui/Card';
import StatusPill from '@/components/ui/StatusPill';
import { MatchedUseCase } from '@/types/types-v3';
import { FeasibilityResult } from '@/lib/engines/feasibility-engine';
import useCaseMappings from '@/data/use-case-mappings.json';

interface BestFitUseCasesProps {
  matchedUseCases: MatchedUseCase[];
  feasibilityResults: FeasibilityResult[];
}

export default function BestFitUseCases({ matchedUseCases, feasibilityResults }: BestFitUseCasesProps) {

  // Group matched use cases by priority
  const immediateUseCases = matchedUseCases
    .filter(uc => uc.priority === 'immediate')
    .sort((a, b) => b.fit_score - a.fit_score);

  const quickWinUseCases = matchedUseCases
    .filter(uc => uc.priority === 'quick_win')
    .sort((a, b) => b.fit_score - a.fit_score);

  const futureUseCases = matchedUseCases
    .filter(uc => uc.priority === 'future')
    .sort((a, b) => b.fit_score - a.fit_score);

  // Get ALL enabled use cases
  const enabledUseCaseIds = new Set(
    (feasibilityResults || []).flatMap(result => result.enabled_use_cases)
  );

  // Find additional enabled capabilities not in matched use cases
  const matchedIds = new Set(matchedUseCases.map(uc => uc.use_case_id));
  const additionalCapabilities: any[] = [];

  (useCaseMappings as any).use_cases.forEach((uc: any) => {
    // Filter out Meetings & Collaboration Licenses category
    if (uc.category === 'Meetings & Collaboration Licenses') {
      return;
    }

    if (enabledUseCaseIds.has(uc.id) && !matchedIds.has(uc.id)) {
      additionalCapabilities.push(uc);
    }
  });

  // Group additional capabilities by category
  const capabilitiesByCategory: Record<string, any[]> = {};
  additionalCapabilities.forEach(uc => {
    if (!capabilitiesByCategory[uc.category]) {
      capabilitiesByCategory[uc.category] = [];
    }
    capabilitiesByCategory[uc.category].push(uc);
  });

  if (matchedUseCases.length === 0 && additionalCapabilities.length === 0) {
    return null;
  }

  const renderUseCaseGrid = (useCases: MatchedUseCase[]) => {
    if (useCases.length === 0) return null;

    return (
      <div className="grid md:grid-cols-2 gap-6">
        {useCases.map(useCase => (
          <Card key={useCase.use_case_id} hover>
            <div className="mb-3">
              <h4 className="text-lg font-bold text-text-primary mb-1">
                {useCase.name}
              </h4>
              <span className="text-sm text-text-tertiary">{useCase.category}</span>
            </div>

            <p className="text-sm text-text-secondary mb-4 line-clamp-2">
              {useCase.description}
            </p>

            {/* Quick Metrics */}
            <div className="grid grid-cols-3 gap-3 mb-4">
              <div>
                <div className="text-lg font-bold text-accent-green">
                  {useCase.estimated_monthly_deflection.toLocaleString()}
                </div>
                <div className="text-xs text-text-tertiary">Tickets</div>
              </div>
              <div>
                <div className="text-lg font-bold text-accent-blue">
                  {Math.round(useCase.estimated_hours_saved).toLocaleString()}h
                </div>
                <div className="text-xs text-text-tertiary">Saved</div>
              </div>
              <div>
                <div className="text-lg font-bold text-text-primary">
                  {useCase.fit_score}
                </div>
                <div className="text-xs text-text-tertiary">Fit Score</div>
              </div>
            </div>

            {/* Implementation Info */}
            <div className="flex items-center justify-between text-xs text-text-tertiary">
              <span>{useCase.time_to_value_days} day setup</span>
              <span className={`font-semibold ${
                useCase.implementation_effort === 'low' ? 'text-accent-green' :
                useCase.implementation_effort === 'medium' ? 'text-accent-blue' :
                'text-accent-orange'
              }`}>
                {useCase.implementation_effort} effort
              </span>
            </div>
          </Card>
        ))}
      </div>
    );
  };

  return (
    <div className="max-w-6xl mx-auto mb-16">
      <div className="mb-8">
        <h2 className="text-3xl font-bold text-text-primary mb-4">
          Your AI Worker Deployment Plan
        </h2>
        <p className="text-text-secondary">
          Organized by when to deploy, based on setup complexity, time-to-value, and business impact.
        </p>
      </div>

      {/* Immediate Deployment (Week 1) */}
      {immediateUseCases.length > 0 && (
        <div className="mb-12">
          <div className="flex items-center gap-3 mb-6">
            <div className="flex-shrink-0 w-12 h-12 bg-accent-green/20 rounded-full flex items-center justify-center">
              <svg className="w-6 h-6 text-accent-green" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
              </svg>
            </div>
            <div>
              <h3 className="text-2xl font-bold text-text-primary">
                Deploy Immediately (Week 1)
              </h3>
              <p className="text-sm text-text-secondary">
                {immediateUseCases.length} AI Worker{immediateUseCases.length !== 1 ? 's' : ''} ready for immediate deployment
              </p>
            </div>
          </div>
          {renderUseCaseGrid(immediateUseCases)}
        </div>
      )}

      {/* Quick Wins (Month 1-2) */}
      {quickWinUseCases.length > 0 && (
        <div className="mb-12">
          <div className="flex items-center gap-3 mb-6">
            <div className="flex-shrink-0 w-12 h-12 bg-accent-blue/20 rounded-full flex items-center justify-center">
              <svg className="w-6 h-6 text-accent-blue" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4M7.835 4.697a3.42 3.42 0 001.946-.806 3.42 3.42 0 014.438 0 3.42 3.42 0 001.946.806 3.42 3.42 0 013.138 3.138 3.42 3.42 0 00.806 1.946 3.42 3.42 0 010 4.438 3.42 3.42 0 00-.806 1.946 3.42 3.42 0 01-3.138 3.138 3.42 3.42 0 00-1.946.806 3.42 3.42 0 01-4.438 0 3.42 3.42 0 00-1.946-.806 3.42 3.42 0 01-3.138-3.138 3.42 3.42 0 00-.806-1.946 3.42 3.42 0 010-4.438 3.42 3.42 0 00.806-1.946 3.42 3.42 0 013.138-3.138z" />
              </svg>
            </div>
            <div>
              <h3 className="text-2xl font-bold text-text-primary">
                Quick Wins (Month 1-2)
              </h3>
              <p className="text-sm text-text-secondary">
                {quickWinUseCases.length} AI Worker{quickWinUseCases.length !== 1 ? 's' : ''} for near-term deployment
              </p>
            </div>
          </div>
          {renderUseCaseGrid(quickWinUseCases)}
        </div>
      )}

      {/* Future Opportunities (Month 3+) */}
      {futureUseCases.length > 0 && (
        <div>
          <div className="flex items-center gap-3 mb-6">
            <div className="flex-shrink-0 w-12 h-12 bg-accent-orange/20 rounded-full flex items-center justify-center">
              <svg className="w-6 h-6 text-accent-orange" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
              </svg>
            </div>
            <div>
              <h3 className="text-2xl font-bold text-text-primary">
                üìÖ Strategic Opportunities (Month 3+)
              </h3>
              <p className="text-sm text-text-secondary">
                {futureUseCases.length} AI Worker{futureUseCases.length !== 1 ? 's' : ''} for longer-term planning
              </p>
            </div>
          </div>
          {renderUseCaseGrid(futureUseCases)}
        </div>
      )}

      {/* Additional Capabilities */}
      {additionalCapabilities.length > 0 && (
        <div className="mt-16">
          <div className="mb-8">
            <h3 className="text-2xl font-bold text-text-primary mb-2">
              More Available Capabilities
            </h3>
            <p className="text-text-secondary">
              {additionalCapabilities.length} additional AI Worker automation{additionalCapabilities.length !== 1 ? 's' : ''} you can deploy
            </p>
          </div>

          <div className="space-y-8">
            {Object.entries(capabilitiesByCategory).map(([category, capabilities]) => (
              <div key={category}>
                <h4 className="text-lg font-semibold text-text-primary mb-4 flex items-center gap-2">
                  <span className="text-highlight">‚ñ∏</span> {category}
                </h4>
                <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {capabilities.map((uc: any) => (
                    <Card key={uc.id} className="!p-4" hover>
                      <h5 className="font-semibold text-text-primary mb-1">{uc.name}</h5>
                      <p className="text-xs text-text-tertiary mb-3 line-clamp-2">{uc.description}</p>
                      <div className="flex items-center justify-between text-xs">
                        <span className="text-text-tertiary">{uc.time_to_value_days}d setup</span>
                        <span className={`font-medium ${
                          uc.implementation_effort === 'low' ? 'text-accent-green' :
                          uc.implementation_effort === 'medium' ? 'text-accent-blue' :
                          'text-accent-orange'
                        }`}>
                          {uc.implementation_effort} effort
                        </span>
                      </div>
                    </Card>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

