archive/reports/components/v3/StackAnaly.content:
'use client';

import { FeasibilityResult } from '@/lib/engines/feasibility-engine';
import { MatchedUseCase } from '@/lib/engines/use-case-matcher';
import Card from '@/components/ui/Card';
import useCaseMappings from '@/data/use-case-mappings.json';

interface StackAnalysisProps {
  feasibilityResults: FeasibilityResult[];
  matchedUseCases: MatchedUseCase[];
}

interface UseCaseInfo {
  id: string;
  name: string;
  category: string;
  description: string;
  estimatedImpact?: number;
  isMatched: boolean;
  isRequired: boolean;
}

export default function StackAnalysis({ feasibilityResults, matchedUseCases }: StackAnalysisProps) {
  return (
    <section className="mb-20">
      <h2 className="text-4xl font-bold text-text-primary mb-4">
        Your AI Worker Capabilities
      </h2>
      <p className="text-xl text-text-tertiary mb-12">
        Here's what AI Workers can automate using your current tools and APIs
      </p>

      <div className="space-y-8">
        {feasibilityResults.map((result, idx) => {
          // Get ALL use cases enabled by this tool from FeasibilityEngine
          const enabledUseCaseIds = new Set(result.enabled_use_cases);

          // Build comprehensive list of use cases
          const allUseCasesForTool: UseCaseInfo[] = [];

          (useCaseMappings as any).use_cases.forEach((uc: any) => {
            if (enabledUseCaseIds.has(uc.id)) {
              // Check if this use case has a volume match (for impact data)
              const matchedUseCase = matchedUseCases.find(muc => muc.use_case_id === uc.id);

              // Determine if tool is required or optional for this use case
              const toolNameNormalized = result.tool.toLowerCase().replace(/[\s-]+/g, '_');
              const isRequired = uc.required_tools?.includes(toolNameNormalized);

              allUseCasesForTool.push({
                id: uc.id,
                name: uc.name,
                category: uc.category,
                description: uc.description,
                estimatedImpact: matchedUseCase?.estimated_hours_saved,
                isMatched: !!matchedUseCase,
                isRequired: isRequired
              });
            }
          });

          // Sort: matched (with impact) first, then alphabetically
          allUseCasesForTool.sort((a, b) => {
            if (a.isMatched && !b.isMatched) return -1;
            if (!a.isMatched && b.isMatched) return 1;
            return a.name.localeCompare(b.name);
          });

          const totalImpact = allUseCasesForTool
            .filter(uc => uc.estimatedImpact)
            .reduce((sum, uc) => sum + (uc.estimatedImpact || 0), 0);

          return (
            <Card key={idx} className="!bg-bg-card !border !border-bg-card-alt/20">
              <div className="flex items-start justify-between mb-6">
                <h3 className="text-2xl font-bold text-text-primary">{result.tool}</h3>
                <span className="px-3 py-1 rounded-full bg-highlight/20 text-highlight text-sm font-medium">
                  {Math.round(result.confidence * 100)}% Confidence
                </span>
              </div>

              {/* Always show APIs if available */}
              {result.available_apis.length > 0 && (
                <div className="mb-6">
                  <h4 className="text-lg font-semibold text-text-primary mb-3 flex items-center gap-2">
                    <span className="text-highlight">‚úì</span> Available APIs
                  </h4>
                  <ul className="space-y-2">
                    {result.available_apis.map((api, i) => (
                      <li key={i} className="text-text-tertiary pl-6">
                        ‚Ä¢ {api}
                      </li>
                    ))}
                  </ul>
                </div>
              )}

              {/* Show ALL AI Worker actions this tool enables */}
              {allUseCasesForTool.length > 0 && (
                <div className="mb-6">
                  <h4 className="text-lg font-semibold text-text-primary mb-3 flex items-center gap-2">
                    <span className="text-highlight">‚úì</span> AI Worker Actions Available ({allUseCasesForTool.length})
                  </h4>
                  <div className="grid md:grid-cols-2 gap-2">
                    {allUseCasesForTool.map((uc, i) => (
                      <div key={uc.id} className="text-text-tertiary pl-6">
                        ‚Ä¢ <span className="font-medium">{uc.name}</span>
                        <span className="text-text-tertiary/60 text-sm ml-2">({uc.category})</span>
                        {!uc.isRequired && (
                          <span className="text-text-tertiary/40 text-xs ml-2">enhances</span>
                        )}
                        {uc.estimatedImpact && (
                          <span className="text-highlight text-sm ml-2">~{Math.round(uc.estimatedImpact)}h/mo</span>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Show high-impact opportunity if we have matched use cases */}
              {totalImpact > 0 && (
                <div className="mb-6 p-4 bg-bg-card-alt/30 rounded-lg">
                  <h4 className="text-lg font-semibold text-text-primary mb-2 flex items-center gap-2">
                    <svg className="w-5 h-5 text-highlight" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                    </svg>
                    High-Impact Opportunities
                  </h4>
                  <p className="text-text-tertiary mb-2">
                    We've identified {allUseCasesForTool.filter(uc => uc.isMatched).length} action{allUseCasesForTool.filter(uc => uc.isMatched).length !== 1 ? 's' : ''} an AI Worker could perform based on your ticket volume
                  </p>
                  <p className="text-highlight font-medium">
                    Estimated impact: ~{Math.round(totalImpact).toLocaleString()} hours/month
                  </p>
                </div>
              )}

            </Card>
          );
        })}
      </div>
    </section>
  );
}

archive/reports/components/AIWorkerCard..content:
import Card from "@/components/ui/Card";
import StatusPill from "@/components/ui/StatusPill";
import { MatchedWorker } from "@/types";
import toolsData from "@/data/tools.json";

interface AIWorkerCardProps {
  worker: MatchedWorker;
  rank?: number;
  isTopWorkflow?: boolean;
}

export default function AIWorkerCard({
  worker,
  rank,
  isTopWorkflow = false,
}: AIWorkerCardProps) {
  // Calculate reduction percentage
  const reductionPercentage = Math.round(
    ((worker.metrics.avgResolutionBefore - worker.metrics.avgResolutionAfter) /
      worker.metrics.avgResolutionBefore) *
      100,
  );

  // Get phase label and status per spec
  const getPhaseLabel = () => {
    switch (worker.deploymentPhase) {
      case "week-1":
        return "Week 1 Ready";
      case "month-2":
        return "Month 2-3";
      case "month-4":
        return "Month 4-6";
      case "month-6":
        return "Month 6+";
    }
  };

  const getPhaseStatus = (): "success" | "active" | "warning" => {
    switch (worker.deploymentPhase) {
      case "week-1":
        return "success";
      case "month-2":
        return "active";
      default:
        return "warning";
    }
  };

  // Format time (hours to hrs/min display)
  const formatTime = (hours: number): string => {
    if (hours < 1) {
      return `${Math.round(hours * 60)} min`;
    }
    return `${hours} hr${hours > 1 ? "s" : ""}`;
  };

  return (
    <Card hover className={isTopWorkflow ? "border-2 border-accent-blue" : ""}>
      {/* Top Workflow Banner */}
      {isTopWorkflow && (
        <div className="bg-accent-blue text-bg-primary text-xs font-bold px-4 py-2 flex items-center gap-2 -m-6 mb-4">
          <span className="text-lg">üèÜ</span>
          YOUR TOP WORKFLOW
        </div>
      )}

      {/* Tool Logos */}
      <div className="flex items-center gap-2 mb-4">
        {worker.requiredTools.slice(0, 3).map((toolId) => {
          const tool = toolsData.tools.find((t) => t.id === toolId);
          if (!tool) return null;
          return (
            <img
              key={toolId}
              src={tool.logo}
              alt={tool.name}
              className="w-8 h-8 rounded bg-bg-elevated p-1"
              title={tool.name}
              onError={(e) => {
                (e.target as HTMLImageElement).style.display = "none";
              }}
            />
          );
        })}
      </div>

      {/* Status Pill */}
      <div className="mb-4">
        <StatusPill status={getPhaseStatus()}>{getPhaseLabel()}</StatusPill>
      </div>

      {/* Title with Rank */}
      {rank && (
        <div className="flex items-center gap-3 mb-3">
          <span className="flex-shrink-0 w-8 h-8 bg-accent-blue rounded-full flex items-center justify-center text-bg-primary font-bold text-sm">
            {rank}
          </span>
          <h3 className="text-xl font-bold text-text-primary">{worker.name}</h3>
        </div>
      )}
      {!rank && (
        <h3 className="text-xl font-bold text-text-primary mb-3">
          {worker.name}
        </h3>
      )}

      {/* Description */}
      <p className="text-text-secondary mb-6 leading-relaxed">
        {worker.description}
      </p>

      {/* Key Metrics - 3-column grid per spec */}
      <div className="grid grid-cols-3 gap-3 mb-6">
        <div className="text-center p-3 bg-accent-green/10 rounded-lg">
          <div className="text-2xl font-bold text-accent-green">
            {worker.estimatedTickets}
          </div>
          <div className="text-xs text-text-tertiary mt-1">Tickets/Month</div>
        </div>

        <div className="text-center p-3 bg-accent-blue/10 rounded-lg">
          <div className="text-2xl font-bold text-accent-blue">
            {worker.timeSaved}
          </div>
          <div className="text-xs text-text-tertiary mt-1">Hours Saved</div>
        </div>

        <div className="text-center p-3 bg-accent-purple/10 rounded-lg">
          <div className="text-2xl font-bold text-accent-purple">
            ${worker.costSaved.toLocaleString()}
          </div>
          <div className="text-xs text-text-tertiary mt-1">
            Cost Saved/Month
          </div>
        </div>
      </div>

      {/* What it automates */}
      <div className="mb-4">
        <h4 className="text-sm font-semibold text-text-primary mb-2">
          What it automates:
        </h4>
        <p className="text-sm text-text-secondary italic">
          {worker.description}
        </p>
      </div>

      {/* Your current state */}
      <div className="bg-bg-primary rounded-lg p-4 mb-4">
        <h4 className="text-sm font-semibold text-text-primary mb-3">
          Your current state:
        </h4>
        <div className="grid grid-cols-2 gap-3 text-sm">
          <div>
            <span className="text-text-tertiary">Volume:</span>
            <span className="ml-2 font-medium text-text-primary">
              {worker.estimatedTickets} tickets/month
            </span>
          </div>
          <div>
            <span className="text-text-tertiary">Avg Resolution:</span>
            <span className="ml-2 font-medium text-text-primary">
              {formatTime(worker.metrics.avgResolutionBefore)}
            </span>
          </div>
        </div>
      </div>

      {/* Before/After Timeline per spec */}
      <div className="mb-6">
        <div className="bg-bg-elevated rounded-lg p-4 mb-3">
          <h5 className="text-xs font-semibold text-text-tertiary uppercase mb-2">
            BEFORE
          </h5>
          <p className="text-sm text-text-secondary mb-2">
            {worker.beforeAfter.before}
          </p>
          <div className="text-xs font-bold text-text-tertiary">
            ‚è±Ô∏è {formatTime(worker.metrics.avgResolutionBefore)}
          </div>
        </div>

        <div className="bg-accent-green/10 rounded-lg p-4 border-2 border-accent-green/20">
          <h5 className="text-xs font-semibold text-accent-green uppercase mb-2">
            AFTER
          </h5>
          <p className="text-sm text-text-primary mb-2">
            {worker.beforeAfter.after}
          </p>
          <div className="text-xs font-bold text-accent-green">
            ‚è±Ô∏è {formatTime(worker.metrics.avgResolutionAfter)}
          </div>
        </div>
      </div>

      {/* With automation summary per spec */}
      <div className="bg-gradient-to-r from-accent-green/10 to-accent-blue/10 rounded-lg p-4 mb-4">
        <h4 className="text-sm font-semibold text-text-primary mb-3 flex items-center gap-2">
          <span className="text-accent-green">üìà</span>
          With automation:
        </h4>
        <div className="space-y-2 text-sm">
          <div className="flex items-start gap-2">
            <span className="text-text-secondary">‚Ä¢ Time saved:</span>
            <span className="font-bold text-accent-green flex-1 text-right">
              {worker.timeSaved} hours/month ({reductionPercentage}% reduction)
            </span>
          </div>
          <div className="flex items-start gap-2">
            <span className="text-text-secondary">‚Ä¢ Tickets deflected:</span>
            <span className="font-bold text-accent-blue flex-1 text-right">
              ~
              {Math.round(
                worker.estimatedTickets * worker.metrics.automationRate,
              )}{" "}
              tickets/month
            </span>
          </div>
        </div>
      </div>

      {/* Why this matters - Value Rationale per spec */}
      <div className="mb-4 pb-4 border-b border-brand-secondary/10">
        <h4 className="text-sm font-semibold text-text-primary mb-2">
          Why this matters:
        </h4>
        <p className="text-sm text-text-secondary leading-relaxed">
          {worker.valueRationale}
        </p>
      </div>

      {/* Setup info footer per spec */}
      <div className="flex items-center justify-between text-xs text-text-tertiary">
        <div className="flex items-center gap-1">
          <span>‚è∞</span>
          <span>Setup: {worker.setupTime}</span>
        </div>
        <div className="flex items-center gap-1">
          <span>üìö</span>
          <span>Learning: {worker.learningPeriod}</span>
        </div>
        <div className="flex items-center gap-1">
          <span
            className={
              worker.configComplexity === "low"
                ? "text-accent-green"
                : worker.configComplexity === "medium"
                  ? "text-yellow-600"
                  : "text-orange-600"
            }
          >
            {worker.configComplexity === "low"
              ? "üü¢"
              : worker.configComplexity === "medium"
                ? "üü°"
                : "üü†"}
          </span>
          <span className="capitalize">{worker.configComplexity} setup</span>
        </div>
      </div>
    </Card>
  );
}

