import CountUp from 'react-countup';

interface ExecutiveSummaryProps {
  score: ReadinessScore;
  assessmentData: AssessmentData;
  estimatedDeflection: number;
  hoursSaved: number;
  fteImpact: number;
}

export default function ExecutiveSummary({
