archive/reports/components/ScoreBreakdow.content:
import Card from "@/components/ui/Card";
import { ReadinessScore } from "@/types";

interface ScoreBreakdownProps {
  scores: ReadinessScore;
  techStackCount: number;
  hasApprovalWorkflows: boolean;
  repetitivePercentage: string;
}

interface CategoryScore {
  name: string;
  score: number;
  maxScore: number;
  description: string;
}

export default function ScoreBreakdown({
  scores,
  techStackCount,
  hasApprovalWorkflows,
  repetitivePercentage,
}: ScoreBreakdownProps) {
  // Map existing scores to 5 new categories
  const calculateCategoryScores = (): CategoryScore[] => {
    // Data & APIs - based on tech stack integrations (out of 100)
    const dataAPIsScore = Math.min(100, (techStackCount / 8) * 100);

    // Process Clarity - based on workflows and repetition (out of 100)
    const processScore = Math.min(100, (scores.workflowScore / 35) * 100);

    // Volume & Variability - based on workflow score and volume (out of 100)
    const volumeScore = Math.min(
      100,
      ((scores.workflowScore + scores.operationalScore) / 60) * 100,
    );

    // Governance & Guardrails - based on operational readiness (out of 100)
    const governanceScore = hasApprovalWorkflows ? 100 : 50;

    // Stack Fit - based on tech stack score (out of 100)
    const stackFitScore = Math.min(100, (scores.techStackScore / 40) * 100);

    return [
      {
        name: "Data & APIs",
        score: Math.round(dataAPIsScore),
        maxScore: 100,
        description: "Baseline APIs and data access enabled",
      },
      {
        name: "Process Clarity",
        score: Math.round(processScore),
        maxScore: 100,
        description: "Well-defined documented workflows",
      },
      {
        name: "Volume & Variability",
        score: Math.round(volumeScore),
        maxScore: 100,
        description: "Consistent volume with quality information",
      },
      {
        name: "Governance & Guardrails",
        score: Math.round(governanceScore),
        maxScore: 100,
        description: "Clear approval policies and audit requirements",
      },
      {
        name: "Stack Fit",
        score: Math.round(stackFitScore),
        maxScore: 100,
        description: "Integrated tools for autonomous worker actions",
      },
    ];
  };

  const categories = calculateCategoryScores();

  return (
    <Card className="h-full">
      <h3 className="text-xl font-bold text-text-primary mb-6">
        Score Breakdown
      </h3>

      <div className="space-y-6">
        {categories.map((category) => {
          const percentage = (category.score / category.maxScore) * 100;

          return (
            <div key={category.name}>
              <div className="flex items-center justify-between mb-2">
                <div>
                  <div className="text-sm font-semibold text-text-primary">
                    {category.name}
                  </div>
                  <div className="text-xs text-text-tertiary">
                    {category.description}
                  </div>
                </div>
                <div className="text-lg font-bold text-text-primary ml-4">
                  {category.score}{" "}
                  <span className="text-text-tertiary text-sm">
                    / {category.maxScore}
                  </span>
                </div>
              </div>

              {/* Progress bar with gradient */}
              <div className="relative h-2 bg-bg-primary rounded-full overflow-hidden">
                <div
                  className="absolute inset-y-0 left-0 rounded-full transition-all duration-1000 ease-out"
                  style={{
                    width: `${percentage}%`,
                    background:
                      "linear-gradient(90deg, #6B7280 0%, #3B82F6 50%, #F97316 100%)",
                  }}
                />
              </div>
            </div>
          );
        })}
      </div>
    </Card>
  );
}

archive/reports/components/v4/GetStarted.content:
import Card from '@/components/ui/Card';
import { MatchedUseCase } from '@/types/types-v3';
import { FeasibilityResult } from '@/lib/engines/feasibility-engine';
import useCaseMappings from '@/data/use-case-mappings.json';

interface GetStartedRoadmapProps {
  matchedUseCases: MatchedUseCase[];
  feasibilityResults: FeasibilityResult[];
}

export default function GetStartedRoadmap({ matchedUseCases, feasibilityResults }: GetStartedRoadmapProps) {

  // Group and prioritize
  const immediateUseCases = matchedUseCases
    .filter(uc => uc.priority === 'immediate')
    .sort((a, b) => b.fit_score - a.fit_score);

  const quickWinUseCases = matchedUseCases
    .filter(uc => uc.priority === 'quick_win')
    .sort((a, b) => b.fit_score - a.fit_score);

  // Find future/additional capabilities to expand into
  const enabledUseCaseIds = new Set(
    (feasibilityResults || []).flatMap(result => result.enabled_use_cases)
  );
  const matchedIds = new Set(matchedUseCases.map(uc => uc.use_case_id));

  const futureCapabilities: any[] = [];
  (useCaseMappings as any).use_cases.forEach((uc: any) => {
    if (enabledUseCaseIds.has(uc.id) && !matchedIds.has(uc.id)) {
      futureCapabilities.push(uc);
    }
  });

  // Sort by implementation effort and time to value
  futureCapabilities.sort((a, b) => {
    const effortOrder: Record<string, number> = { low: 0, medium: 1, high: 2 };
    if (effortOrder[a.implementation_effort] !== effortOrder[b.implementation_effort]) {
      return effortOrder[a.implementation_effort] - effortOrder[b.implementation_effort];
    }
    return a.time_to_value_days - b.time_to_value_days;
  });

  if (matchedUseCases.length === 0 && futureCapabilities.length === 0) {
    return null;
  }

  const renderPhase = (
    title: string,
    timeframe: string,
    useCases: MatchedUseCase[],
    phaseColor: string,
    icon: string
  ) => {
    if (useCases.length === 0) return null;

    const totalTickets = useCases.reduce((sum, uc) => sum + uc.estimated_monthly_deflection, 0);
    const totalHours = useCases.reduce((sum, uc) => sum + uc.estimated_hours_saved, 0);

    return (
      <div className="relative">
        <Card>
          <div className="flex items-start gap-4 mb-6">
            <div className={`flex-shrink-0 w-12 h-12 ${phaseColor} rounded-full flex items-center justify-center relative z-10`}>
              <svg className="w-6 h-6 text-current" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                {icon === 'üöÄ' && <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />}
                {icon === '‚ö°' && <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />}
                {icon === 'üìà' && <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" />}
              </svg>
            </div>
            <div className="flex-1">
              <h3 className="text-xl font-bold text-text-primary mb-1">{title}</h3>
              <p className="text-sm text-text-tertiary">{timeframe}</p>
            </div>
            <div className="text-right">
              <div className="text-2xl font-bold text-accent-blue">{totalTickets.toLocaleString()} tickets/month</div>
              <div className="text-sm font-semibold text-text-primary">{Math.round(totalHours).toLocaleString()} hrs saved/month</div>
            </div>
          </div>

          <div className="space-y-4">
            {useCases.map((useCase, index) => (
              <div key={useCase.use_case_id} className="p-4 bg-bg-primary rounded-lg border border-brand-secondary/10">
                <div className="flex items-start justify-between mb-2">
                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-1">
                      <span className="text-sm font-semibold text-accent-blue">
                        Step {index + 1}
                      </span>
                      <h4 className="text-base font-bold text-text-primary">
                        {useCase.name}
                      </h4>
                    </div>
                    <p className="text-sm text-text-secondary line-clamp-1">
                      {useCase.description}
                    </p>
                  </div>
                </div>

                <div className="grid grid-cols-2 md:grid-cols-4 gap-3 mt-3">
                  <div>
                    <div className="text-sm font-bold text-accent-green">
                      {useCase.estimated_monthly_deflection.toLocaleString()}
                    </div>
                    <div className="text-xs text-text-tertiary">Tickets</div>
                  </div>
                  <div>
                    <div className="text-sm font-bold text-accent-blue">
                      {Math.round(useCase.estimated_hours_saved).toLocaleString()}h
                    </div>
                    <div className="text-xs text-text-tertiary">Saved</div>
                  </div>
                  <div>
                    <div className="text-sm font-bold text-text-primary">
                      {useCase.time_to_value_days}d
                    </div>
                    <div className="text-xs text-text-tertiary">Setup</div>
                  </div>
                  <div>
                    <div className={`text-sm font-bold ${
                      useCase.implementation_effort === 'low' ? 'text-accent-green' :
                      useCase.implementation_effort === 'medium' ? 'text-accent-blue' :
                      'text-accent-orange'
                    }`}>
                      {useCase.implementation_effort}
                    </div>
                    <div className="text-xs text-text-tertiary">Effort</div>
                  </div>
                </div>

                {/* Prerequisites Preview */}
                {useCase.prerequisites.length > 0 && (
                  <div className="mt-3 pt-3 border-t border-brand-secondary/10">
                    <details className="group">
                      <summary className="text-xs text-accent-blue cursor-pointer hover:underline list-none flex items-center gap-1">
                        <svg className="w-3 h-3 transition-transform group-open:rotate-90" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                        </svg>
                        View {useCase.prerequisites.length} prerequisite{useCase.prerequisites.length !== 1 ? 's' : ''}
                      </summary>
                      <ul className="mt-2 space-y-1 ml-4">
                        {useCase.prerequisites.map((prereq, i) => (
                          <li key={i} className="text-xs text-text-secondary flex items-start gap-1">
                            <span className="text-accent-green mt-0.5">‚úì</span>
                            {prereq}
                          </li>
                        ))}
                      </ul>
                    </details>
                  </div>
                )}
              </div>
            ))}
          </div>
        </Card>
      </div>
    );
  };

  // Calculate cumulative impact
  const allPriorityUseCases = [...immediateUseCases, ...quickWinUseCases];
  const cumulativeTickets = allPriorityUseCases.reduce((sum, uc) => sum + uc.estimated_monthly_deflection, 0);
  const cumulativeHours = allPriorityUseCases.reduce((sum, uc) => sum + uc.estimated_hours_saved, 0);
  const cumulativeFTE = (cumulativeHours * 12) / 2000;
  const cumulativeValue = cumulativeFTE * 100000; // $100K fully loaded cost

  return (
    <div className="max-w-4xl mx-auto mb-16">
      <div className="text-center mb-12">
        <h2 className="text-3xl font-bold text-text-primary mb-4">
          How to Get Started
        </h2>
        <p className="text-text-secondary max-w-2xl mx-auto">
          A phased deployment plan showing when to deploy each AI Worker, based on your current capabilities and business impact.
        </p>
      </div>

      <div className="space-y-8">
        {renderPhase(
          'Week 1: Quick Deploy',
          'Deploy in first 7 days',
          immediateUseCases,
          'bg-accent-green/20',
          'üöÄ'
        )}

        {renderPhase(
          'Month 1-2: Build Momentum',
          'Deploy over next 4-8 weeks',
          quickWinUseCases,
          'bg-accent-blue/20',
          '‚ö°'
        )}

        {/* Phase 3: Scale & Expand */}
        {futureCapabilities.length > 0 && (
          <div className="relative">
            <Card>
              <div className="flex items-start gap-4 mb-6">
                <div className="flex-shrink-0 w-12 h-12 bg-accent-orange/20 rounded-full flex items-center justify-center relative z-10">
                  <svg className="w-6 h-6 text-current" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" />
                  </svg>
                </div>
                <div className="flex-1">
                  <h3 className="text-xl font-bold text-text-primary mb-1">
                    Month 3+: Scale & Expand
                  </h3>
                  <p className="text-sm text-text-tertiary">
                    Expand automation across more workflows
                  </p>
                </div>
                <div className="text-right">
                  <div className="text-2xl font-bold text-accent-orange">{futureCapabilities.length}</div>
                  <div className="text-xs text-text-tertiary">more AI Workers</div>
                </div>
              </div>

              <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
                {futureCapabilities.map((uc: any) => (
                  <div key={uc.id} className="p-4 bg-bg-primary rounded-lg border border-brand-secondary/10">
                    <h4 className="text-sm font-bold text-text-primary mb-1">
                      {uc.name}
                    </h4>
                    <p className="text-xs text-text-secondary line-clamp-2 mb-3">
                      {uc.description}
                    </p>
                    <div className="flex items-center justify-between text-xs">
                      <span className="text-text-tertiary">{uc.category}</span>
                      <span className={`font-medium ${
                        uc.implementation_effort === 'low' ? 'text-accent-green' :
                        uc.implementation_effort === 'medium' ? 'text-accent-blue' :
                        'text-accent-orange'
                      }`}>
                        {uc.time_to_value_days}d ‚Ä¢ {uc.implementation_effort} effort
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            </Card>
          </div>
        )}
      </div>

      {/* Cumulative Impact Card */}
      {allPriorityUseCases.length > 0 && (
        <div className="mt-12" style={{ boxShadow: '0 0 20px rgba(130, 216, 149, 0.3)', borderRadius: '12px' }}>
          <Card>
            <div className="text-center mb-6">
              <h3 className="text-xl font-bold text-text-primary mb-2">
                Cumulative Impact (First 2 Months)
              </h3>
              <p className="text-sm text-text-secondary">
                By deploying AI workers with these {allPriorityUseCases.length} skills
              </p>
            </div>

            <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
              <div className="text-center">
                <div className="text-3xl font-bold text-accent-green mb-1">
                  {cumulativeTickets.toLocaleString()}
                </div>
                <div className="text-xs text-text-tertiary">Tickets Automated</div>
              </div>
              <div className="text-center">
                <div className="text-3xl font-bold text-accent-blue mb-1">
                  {Math.round(cumulativeHours).toLocaleString()}
                </div>
                <div className="text-xs text-text-tertiary">Hours Saved/Month</div>
              </div>
              <div className="text-center">
                <div className="text-3xl font-bold text-text-primary mb-1">
                  {cumulativeFTE.toFixed(1)}
                </div>
                <div className="text-xs text-text-tertiary">FTE Capacity Freed</div>
              </div>
              <div className="text-center">
                <div className="text-3xl font-bold text-accent-orange mb-1">
                  ${Math.round(cumulativeValue / 1000).toLocaleString()}K
                </div>
                <div className="text-xs text-text-tertiary">Annual Value</div>
              </div>
            </div>
          </Card>
        </div>
      )}

      {/* Next Steps CTA */}
      <div className="mt-8 text-center">
        <p className="text-sm text-text-tertiary mb-4">
          Ready to start your deployment?
        </p>
        <a 
          href="https://www.ai.work/book-a-demo" 
          target="_blank" 
          rel="noopener noreferrer"
          className="px-6 py-3 bg-white text-black rounded-lg font-semibold hover:bg-gray-100 transition-colors inline-flex items-center gap-2"
        >
          <span className="w-2 h-2 rounded-full bg-highlight"></span>
          Schedule Implementation Planning Call
        </a>
      </div>
    </div>
  );
}

archive/reports/components/v3/ROIBreakdo.content:
'use client';

import { ROIResult } from '@/lib/engines/roi-calculator';
import { FeasibilityResult } from '@/lib/engines/feasibility-engine';
import Card from '@/components/ui/Card';
import useCaseMappings from '@/data/use-case-mappings.json';

interface ROIBreakdownProps {
  roiResult: ROIResult;
  feasibilityResults: FeasibilityResult[];
}

export default function ROIBreakdown({ roiResult, feasibilityResults }: ROIBreakdownProps) {
  // Get all enabled use case IDs
  const enabledUseCaseIds = new Set(
    (feasibilityResults || []).flatMap(result => result.enabled_use_cases)
  );

  // Get categories from matched use cases (actual impact)
  const matchedCategories = new Set(
    roiResult.breakdown_by_category.map(cat => cat.category)
  );

  // Find additional categories with enabled use cases
  const additionalCategories: Record<string, {
    category: string;
    useCases: any[];
    estimatedTickets: number;
    estimatedHours: number;
  }> = {};

  (useCaseMappings as any).use_cases.forEach((uc: any) => {
    // Filter out Meetings & Collaboration Licenses category
    if (uc.category === 'Meetings & Collaboration Licenses') {
      return;
    }

    if (enabledUseCaseIds.has(uc.id) && !matchedCategories.has(uc.category)) {
      if (!additionalCategories[uc.category]) {
        additionalCategories[uc.category] = {
          category: uc.category,
          useCases: [],
          estimatedTickets: 0,
          estimatedHours: 0
        };
      }
      additionalCategories[uc.category].useCases.push(uc);
      // Estimate based on typical values (assuming 1000 ticket baseline)
      const estTickets = Math.round((uc.typical_volume_pct || 0.05) * 1000 * (uc.automation_rate || 0.8));
      const estHours = estTickets * (uc.typical_ttr_hours || 1.5);
      additionalCategories[uc.category].estimatedTickets += estTickets;
      additionalCategories[uc.category].estimatedHours += estHours;
    }
  });
  return (
    <section className="mb-20">
      <h2 className="text-4xl font-bold text-text-primary mb-4">
        ROI Breakdown
      </h2>
      <p className="text-xl text-text-tertiary mb-12">
        Detailed impact analysis by category
      </p>

      {/* Summary Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-12">
        <Card className="!bg-bg-card !border !border-bg-card-alt/20">
          <div className="text-sm text-text-tertiary mb-2">Automatable Tickets</div>
          <div className="text-3xl font-bold text-highlight">
            {roiResult.automatable_tickets.toLocaleString()}
            <span className="text-lg text-text-tertiary ml-2">
              ({roiResult.automatable_pct.toFixed(1)}%)
            </span>
          </div>
        </Card>

        <Card className="!bg-bg-card !border !border-bg-card-alt/20">
          <div className="text-sm text-text-tertiary mb-2">Hours Saved/Month</div>
          <div className="text-3xl font-bold text-highlight">
            {Math.round(roiResult.total_hours_saved).toLocaleString()}
          </div>
        </Card>

        <Card className="!bg-bg-card !border !border-bg-card-alt/20">
          <div className="text-sm text-text-tertiary mb-2">FTE Equivalent</div>
          <div className="text-3xl font-bold text-highlight">
            {roiResult.fte_equivalent.toFixed(1)}
          </div>
        </Card>

        <Card className="!bg-bg-card !border !border-bg-card-alt/20">
          <div className="text-sm text-text-tertiary mb-2">Annual Value</div>
          <div className="text-3xl font-bold text-highlight">
            ${Math.round(roiResult.annual_value_usd / 1000).toLocaleString()}K
          </div>
        </Card>
      </div>

      {/* Category Breakdown */}
      <Card className="!bg-bg-card !border !border-bg-card-alt/20">
        <h3 className="text-2xl font-bold text-text-primary mb-6">
          Impact by Category
        </h3>

        {/* Matched Categories with Actual Impact */}
        <div className="space-y-6 mb-8">
          {roiResult.breakdown_by_category
            .filter(cat => cat.category !== 'Meetings & Collaboration Licenses')
            .map((category, idx) => (
            <div key={idx} className="border-b border-bg-card-alt/20 pb-6">
              <div className="flex items-start justify-between mb-3">
                <div>
                  <h4 className="text-lg font-semibold text-text-primary">
                    {category.category}
                  </h4>
                  <p className="text-sm text-text-tertiary">
                    {category.tickets.toLocaleString()} tickets/month automated
                  </p>
                </div>
                <span className="px-3 py-1 rounded-full bg-highlight/20 text-highlight text-sm font-medium">
                  {Math.round(category.confidence * 100)}% confidence
                </span>
              </div>

              <div className="flex items-center gap-4">
                <div className="flex-1">
                  <div className="text-2xl font-bold text-highlight">
                    {Math.round(category.hours_saved).toLocaleString()} hours/month
                  </div>
                  <div className="text-sm text-text-tertiary">
                    ~{((category.hours_saved * 12) / 2000).toFixed(1)} FTE saved annually
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Additional Categories with Estimated Potential */}
        {Object.keys(additionalCategories).length > 0 && (
          <>
            <div className="border-t border-bg-card-alt/20 pt-8 mb-6">
              <h4 className="text-lg font-semibold text-text-primary mb-2">
                Additional Automation Potential
              </h4>
              <p className="text-sm text-text-tertiary">
                More categories you can automate with your current capabilities
              </p>
            </div>

            <div className="space-y-6">
              {Object.values(additionalCategories).map((cat, idx) => (
                <div key={idx} className="border-b border-bg-card-alt/20 last:border-0 pb-6 last:pb-0">
                  <div className="flex items-start justify-between mb-3">
                    <div>
                      <h5 className="text-lg font-semibold text-text-primary">
                        {cat.category}
                      </h5>
                      <p className="text-sm text-text-tertiary">
                        {cat.useCases.length} AI Worker{cat.useCases.length !== 1 ? 's' : ''} available
                      </p>
                    </div>
                    <span className="px-3 py-1 rounded-full bg-accent-blue/20 text-accent-blue text-sm font-medium">
                      Estimated
                    </span>
                  </div>

                  <div className="flex items-center gap-4">
                    <div className="flex-1">
                      <div className="text-2xl font-bold text-accent-blue">
                        ~{Math.round(cat.estimatedHours)} hours/month
                      </div>
                      <div className="text-sm text-text-tertiary">
                        Potential savings based on typical use patterns
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </>
        )}

        <div className="mt-8 p-4 bg-bg-card-alt/30 rounded-lg">
          <p className="text-sm text-text-tertiary">
            <span className="font-semibold text-text-primary">Overall Confidence: {roiResult.confidence}%</span>
            {' '}‚Äî Based on weighted average of use case confidence scores and your ticket volumes
          </p>
        </div>
      </Card>
    </section>
  );
}

