          <div className="text-6xl font-bold text-white">
            <CountUp end={hoursSaved} duration={2} separator="," />
          </div>
          <div className="text-sm text-gray-500 mt-2">Per year</div>
        </div>

        {/* Cost Savings */}
        <div className="border-r border-gray-800 px-8 py-10">
          <div className="text-xs uppercase tracking-wide text-gray-500 mb-6 leading-tight">
            Reduce Operational<br />Cost By
          </div>
          <div className="text-6xl font-bold text-white">
            <CountUp
              end={Math.round((costSavings / (costSavings + 10000)) * 100)}
              duration={2}
            />
            %
          </div>
        </div>

        {/* FTE Equivalent */}
        <div className="px-8 py-10">
          <div className="text-xs uppercase tracking-wide text-gray-500 mb-6">
            FTE Equivalent
          </div>
          <div className="text-6xl font-bold text-white">
            <CountUp end={fteEquivalent} duration={2} decimals={1} />
          </div>
          <div className="text-sm text-gray-500 mt-2">
            Full-time employees
          </div>
        </div>
      </div>

      {/* Confidence Note */}
      <p className="text-sm text-text-tertiary text-center mt-4">
        Confidence: {confidence} - Based on your self-reported data
      </p>
    </div>
  );
}

